      font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', sans-serif;
      font-size: 12pt;
      font-weight: bold;
      color: #1f2937;
    }

    /* Footer with page numbers */
    @bottom-right {
      content: "í˜ì´ì§€ " counter(page) " / " counter(pages);
      font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', sans-serif;
      font-size: 10pt;
      color: #6b7280;
    }

    @bottom-left {
      content: "ì¸ì‡„ì¼ì‹œ: " attr(data-print-date);
      font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', sans-serif;
      font-size: 10pt;
      color: #6b7280;
    }
  }

  /* Portrait orientation */
  @page portrait {
    size: A4 portrait;
  }

  /* Landscape orientation */
  @page landscape {
    size: A4 landscape;
    margin: 10mm 15mm 10mm 15mm;
  }

  /* Named page layouts */
  @page items-list {
    size: A4 landscape;
    margin: 15mm 10mm 15mm 10mm;
  }

  @page companies-list {
    size: A4 portrait;
    margin: 15mm 10mm 15mm 10mm;
  }

  @page transactions-report {
    size: A4 portrait;
    margin: 20mm 15mm 20mm 15mm;
  }

  /* Hide non-printable elements */
  .no-print,
  .print-hidden,
  nav,
  aside,
  header:not(.print-header),
  footer:not(.print-footer),
  .sidebar,
  .header,
  button,
  .btn,
  .modal,
  .toast,
  .tooltip,
  .dropdown,
  .pagination,
  .search-bar,
  .filters,
  .action-buttons,
  .edit-controls,
  .mobile-menu,
  .dark-mode-toggle,
  .notifications,
  .user-menu,
  .breadcrumb,
  .back-button,
  .loading-spinner {
    display: none !important;
  }

  /* Body styles */
  body {
    background: white !important;
    color: #1f2937 !important;
    font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', 'Apple SD Gothic Neo', sans-serif;
    font-size: 11pt;
    line-height: 1.4;
    margin: 0;
    padding: 0;
  }

  /* Container styles */
  .print-container {
    width: 100% !important;
    max-width: none !important;
    margin: 0 !important;
    padding: 0 !important;
    background: white !important;
  }

  /* Print Header */
  .print-header {
    display: block !important;
    text-align: center;
    margin-bottom: 20pt;
    padding-bottom: 10pt;
    border-bottom: 2pt solid #1f2937;
  }

  .print-header h1 {
    font-size: 18pt;
    font-weight: bold;
    color: #1f2937 !important;
    margin: 0 0 8pt 0;
    font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', sans-serif;
  }

  .print-header .company-info {
    font-size: 12pt;
    color: #4b5563 !important;
    margin-bottom: 4pt;
  }

  .print-header .report-title {
    font-size: 14pt;
    font-weight: bold;
    color: #1f2937 !important;
    margin-top: 8pt;
  }

  .print-header .print-date {
    font-size: 10pt;
    color: #6b7280 !important;
    margin-top: 4pt;
  }

  /* Table styles */
  table {
    width: 100% !important;
    border-collapse: collapse !important;
    margin: 0 0 15pt 0 !important;
    background: white !important;
    page-break-inside: avoid;
  }

  table.print-table {
    font-size: 9pt;
    border: 1pt solid #374151 !important;
  }

  table.print-table th {
    background: #f3f4f6 !important;
    color: #1f2937 !important;
    font-weight: bold;
    padding: 6pt 4pt !important;
    border: 1pt solid #374151 !important;
    text-align: left;
    vertical-align: middle;
    font-size: 9pt;
  }

  table.print-table td {
    padding: 4pt !important;
    border: 1pt solid #d1d5db !important;
    color: #1f2937 !important;
    vertical-align: top;
    font-size: 9pt;
    line-height: 1.3;
  }

  /* Striped table rows */
  table.print-table tbody tr:nth-child(even) {
    background: #f9fafb !important;
  }

  table.print-table tbody tr:nth-child(odd) {
    background: white !important;
  }

  /* Table alignment */
  .text-center { text-align: center !important; }
  .text-right { text-align: right !important; }
  .text-left { text-align: left !important; }

  /* Number formatting */
  .number {
    text-align: right !important;
    font-family: 'Consolas', 'Monaco', monospace;
  }

  .currency {
    text-align: right !important;
    font-family: 'Consolas', 'Monaco', monospace;
  }

  /* Status badges in print */
  .status-badge {
    display: inline-block;
    padding: 1pt 4pt;
    border: 1pt solid #d1d5db;
    border-radius: 2pt;
    font-size: 8pt;
