  }

  .page-break-inside-avoid {
    page-break-inside: avoid;
  }

  /* Avoid page breaks in table rows */
  tr {
    page-break-inside: avoid;
  }

  /* Summary sections */
  .print-summary {
    margin: 15pt 0;
    padding: 8pt;
    border: 1pt solid #d1d5db;
    background: #f9fafb !important;
    page-break-inside: avoid;
  }

  .print-summary h3 {
    margin: 0 0 8pt 0;
    font-size: 12pt;
    font-weight: bold;
    color: #1f2937 !important;
  }

  .print-summary .summary-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150pt, 1fr));
    gap: 8pt;
  }

  .print-summary .summary-item {
    display: flex;
    justify-content: space-between;
    padding: 2pt 0;
    border-bottom: 0.5pt dotted #d1d5db;
  }

  .print-summary .summary-label {
    font-weight: bold;
    color: #374151 !important;
  }

  .print-summary .summary-value {
    color: #1f2937 !important;
    text-align: right;
  }

  /* Print metadata */
  .print-metadata {
    margin-top: 20pt;
    padding-top: 10pt;
    border-top: 1pt solid #e5e7eb;
    font-size: 9pt;
    color: #6b7280 !important;
    text-align: center;
  }

  /* Korean typography optimization */
  h1, h2, h3, h4, h5, h6 {
    font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', 'Apple SD Gothic Neo', sans-serif;
    font-weight: bold;
    color: #1f2937 !important;
    margin: 8pt 0 4pt 0;
  }

  /* Specific layouts for different report types */

  /* Items List Layout */
  .items-print-layout {
    page: items-list;
  }

  .items-print-layout table {
    font-size: 8pt;
  }

  .items-print-layout th,
  .items-print-layout td {
    padding: 3pt 2pt !important;
    font-size: 8pt;
  }

  /* Companies List Layout */
  .companies-print-layout {
    page: companies-list;
  }

  /* Transactions Report Layout */
  .transactions-print-layout {
    page: transactions-report;
  }

  /* BOM Structure Layout */
  .bom-print-layout .bom-level-0 {
    font-weight: bold;
    background: #f3f4f6 !important;
  }

  .bom-print-layout .bom-level-1 {
    padding-left: 10pt !important;
  }

  .bom-print-layout .bom-level-2 {
    padding-left: 20pt !important;
  }

  .bom-print-layout .bom-level-3 {
    padding-left: 30pt !important;
  }

  /* Stock Report specific styles */
  .stock-report .low-stock {
    background: #fef2f2 !important;
    color: #dc2626 !important;
  }

  .stock-report .out-of-stock {
    background: #fee2e2 !important;
    color: #991b1b !important;
    font-weight: bold;
  }

  /* Print utilities */
  .print-only {
    display: block !important;
  }

  .no-break {
    page-break-inside: avoid;
    break-inside: avoid;
  }

  /* Responsive print table */
  .print-responsive-table {
    font-size: 8pt;
  }

  @media print and (max-width: 210mm) {
    .print-responsive-table {
      font-size: 7pt;
    }

    .print-responsive-table th,
    .print-responsive-table td {
      padding: 2pt 1pt !important;
    }
  }

  /* Print landscape table adjustments */
  .print-landscape-table {
    width: 100%;
    font-size: 8pt;
  }

  .print-landscape-table th,
  .print-landscape-table td {
    padding: 3pt 2pt !important;
    font-size: 8pt;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  /* Footer information */
  .print-footer {
    display: block !important;
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    height: 15mm;
    background: white;
    border-top: 1pt solid #e5e7eb;
    padding: 2mm 0;
    text-align: center;
    font-size: 9pt;
    color: #6b7280 !important;
  }

  /* Print signature section */
  .print-signature {
    margin-top: 30pt;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 20pt;
    page-break-inside: avoid;
  }

  .print-signature .signature-box {
    text-align: center;
    border-top: 1pt solid #374151;
    padding-top: 5pt;
    margin-top: 15pt;
  }

  .print-signature .signature-label {
    font-size: 10pt;
    font-weight: bold;
    color: #374151 !important;
  }

  /* Korean number formatting */
  .korean-number::after {
    content: "";
  }
}

/* Screen preview styles for print preview */
.print-preview {
