/**
 * ì¸ì‡„ ë²„íŠ¼ ì»´í¬ë„ŒíŠ¸
 */
export default function PrintButton({
  data,
  columns,
  title = 'ë°ì´í„° ëª©ë¡',
  subtitle,
  variant = 'default',
  size = 'md',
  showPreview = false,
  showOptions = true,
  orientation = 'landscape',
  className = '',
  disabled = false,
  children,
  onPrintStart,
  onPrintComplete,
  onError
}: PrintButtonProps) {
  const [isLoading, setIsLoading] = useState(false);
  const [showOptionsModal, setShowOptionsModal] = useState(false);

  const defaultOptions: PrintOptions = {
    orientation,
    pageSize: 'A4',
    includeHeader: true,
    includeFooter: true,
    showPageNumbers: true,
    title,
    subtitle
  };

  const getSizeClasses = () => {
    switch (size) {
      case 'sm':
        return 'px-3 py-1.5 text-sm';
      case 'lg':
        return 'px-6 py-3 text-lg';
      default:
        return 'px-4 py-2 text-base';
    }
  };

  const getIconSize = () => {
    switch (size) {
      case 'sm':
        return 'w-4 h-4';
      case 'lg':
        return 'w-6 h-6';
      default:
        return 'w-5 h-5';
    }
  };

  const handlePrint = async (options: PrintOptions = defaultOptions) => {
    if (disabled || data.length === 0) return;

    try {
      setIsLoading(true);
      onPrintStart?.();

      if (showPreview) {
        await printWithPreview(data, columns, options);
      } else {
        await printTable(data, columns, options);
      }

      onPrintComplete?.();
    } catch (error) {
      console.error('Print error:', error);
      onError?.(error as Error);

      // ì‚¬ìš©ìì—ê²Œ ì˜¤ë¥˜ ë©”ì‹œì§€ í‘œì‹œ
      alert(`ì¸ì‡„ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: ${error instanceof Error ? error.message : 'ì•Œ ìˆ˜ ì—†ëŠ” ì˜¤ë¥˜'}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDirectPrint = () => {
    if (showOptions) {
      setShowOptionsModal(true);
    } else {
      handlePrint();
    }
  };

  const renderButton = () => {
    const baseClasses = `
      inline-flex items-center justify-center gap-2 font-medium rounded-lg transition-colors
      disabled:opacity-50 disabled:cursor-not-allowed
      ${getSizeClasses()}
      ${className}
    `;

    const iconClass = getIconSize();

    if (variant === 'icon') {
      return (
        <button
          onClick={handleDirectPrint}
          disabled={disabled || isLoading || data.length === 0}
          className={`${baseClasses} bg-gray-500 text-white hover:bg-gray-600 dark:bg-gray-600 dark:hover:bg-gray-700`}
          title="ì¸ì‡„"
        >
          {isLoading ? (
            <div className={`${iconClass} border-2 border-white border-t-transparent rounded-full animate-spin`} />
          ) : (
            children || <Printer className={iconClass} />
          )}
        </button>
      );
    }

    if (variant === 'text') {
      return (
        <button
          onClick={handleDirectPrint}
          disabled={disabled || isLoading || data.length === 0}
          className={`${baseClasses} text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300`}
        >
          {isLoading ? (
            <>
              <div className={`${iconClass} border-2 border-blue-600 border-t-transparent rounded-full animate-spin`} />
              ì¸ì‡„ ì¤‘...
            </>
          ) : (
            <>
              <Printer className={iconClass} />
              ì¸ì‡„
            </>
          )}
        </button>
      );
    }

    return (
      <button
        onClick={handleDirectPrint}
        disabled={disabled || isLoading || data.length === 0}
        className={`${baseClasses} bg-gray-500 text-white hover:bg-gray-600 dark:bg-gray-600 dark:hover:bg-gray-700`}
      >
        {isLoading ? (
          <>
            <div className={`${iconClass} border-2 border-white border-t-transparent rounded-full animate-spin`} />
            ì¸ì‡„ ì¤‘...
          </>
        ) : (
          <>
            <Printer className={iconClass} />
            ì¸ì‡„
          </>
        )}
      </button>
    );
  };

  const isEmpty = data.length === 0;

  return (
    <>
      <div className="relative">
        {renderButton()}

        {/* ë°ì´í„°ê°€ ì—†ì„ ë•Œ íˆ´íŒ */}
        {isEmpty && (
          <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-2 py-1 bg-gray-800 text-white text-xs rounded opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all">
            ì¸ì‡„í•  ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤
          </div>
        )}
      </div>

      {/* ì¸ì‡„ ì˜µì…˜ ëª¨ë‹¬ */}
      <PrintOptionsModal
        isOpen={showOptionsModal}
        onClose={() => setShowOptionsModal(false)}
        onPrint={handlePrint}
        currentOptions={defaultOptions}
      />
    </>
  );
}

/**
