'use client';

import { useState, useEffect, useCallback } from 'react';
import { Network, Plus, Search, Edit2, Trash2, Filter, Copy } from 'lucide-react';
import Modal from '@/components/Modal';
import BOMForm from '@/components/BOMForm';
import { useToast } from '@/contexts/ToastContext';
import { useConfirm } from '@/hooks/useConfirm';
import { BOMExportButton } from '@/components/ExcelExportButton';
import PrintButton from '@/components/PrintButton';

interface BOM {
  bom_id: number;
  parent_item_id: number;
  child_item_id: number;
  parent_item_name?: string;
  child_item_name?: string;
  parent_item_code?: string;
  child_item_code?: string;
  quantity: number;
  level: number;
  notes?: string;
  is_active: boolean;
}

export default function BOMPage() {
  const [bomData, setBomData] = useState<BOM[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedParentItem, setSelectedParentItem] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingBOM, setEditingBOM] = useState<BOM | null>(null);
  const [showActiveOnly, setShowActiveOnly] = useState(true);
  const [items, setItems] = useState<any[]>([]);
  const [deletingBomId, setDeletingBomId] = useState<number | null>(null);
  const { success, error } = useToast();
  const { deleteWithToast, ConfirmDialog } = useConfirm();

  // ì¸ì‡„ìš© ì»¬ëŸ¼ ì •ì˜
  const printColumns = [
    { key: 'level_display', label: 'ë ˆë²¨', align: 'left' as const, width: '8%' },
    { key: 'parent_item_code', label: 'ëª¨í’ˆë²ˆ', align: 'left' as const, width: '15%' },
    { key: 'parent_item_name', label: 'ëª¨í’ˆëª…', align: 'left' as const, width: '20%' },
    { key: 'child_item_code', label: 'ìí’ˆë²ˆ', align: 'left' as const, width: '15%' },
    { key: 'child_item_name', label: 'ìí’ˆëª…', align: 'left' as const, width: '20%' },
    { key: 'quantity', label: 'ì†Œìš”ëŸ‰', align: 'right' as const, width: '10%', type: 'number' as const },
    { key: 'unit', label: 'ë‹¨ìœ„', align: 'center' as const, width: '6%' },
    { key: 'notes', label: 'ë¹„ê³ ', align: 'left' as const, width: '6%' }
  ];

  const fetchItems = useCallback(async () => {
    try {
      const response = await fetch('/api/items');
      const data = await response.json();
      if (data.success) {
        setItems(data.data.items || []);
      }
    } catch (error) {
      console.error('Failed to fetch items:', error);
    }
  }, []);

  const fetchBOMData = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (selectedParentItem) params.append('parent_item_id', selectedParentItem);

      const response = await fetch(`/api/bom?${params}`);
      const data = await response.json();

      if (data.success) {
        // Handle nested API response structure
        const bomArray = data.data.bomEntries || [];

        // Transform API response to match component interface
        const transformedBOM = bomArray.map((item: any) => ({
          ...item,
          parent_item_name: item.parent_item?.item_name || '',
          parent_item_code: item.parent_item?.item_code || '',
          child_item_name: item.child_item?.item_name || '',
          child_item_code: item.child_item?.item_code || '',
        }));

        // Filter active items if needed
        const bomList = showActiveOnly
          ? transformedBOM.filter((item: BOM) => item.is_active)
          : transformedBOM;

        setBomData(bomList);
      }
    } catch (error) {
      console.error('Failed to fetch BOM data:', error);
    } finally {
      setLoading(false);
    }
  }, [selectedParentItem, showActiveOnly]);

  useEffect(() => {
    fetchBOMData();
    fetchItems();
  }, [fetchBOMData, fetchItems]);


  const handleDelete = async (bom: BOM) => {
    const deleteAction = async () => {
      setDeletingBomId(bom.bom_id);
      try {
        const response = await fetch(`/api/bom?bom_id=${bom.bom_id}`, {
          method: 'DELETE'
        });

        if (!response.ok) {
          const data = await response.json();
          throw new Error(data.error || 'BOM ì‚­ì œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
        }

        fetchBOMData();
      } catch (err) {
        console.error('Failed to delete BOM item:', err);
        throw err;
      } finally {
        setDeletingBomId(null);
      }
    };

    await deleteWithToast(deleteAction, {
      title: 'BOM ì‚­ì œ',
      itemName: `${bom.parent_item_name || 'ì•Œ ìˆ˜ ì—†ëŠ” í’ˆëª©'} â†’ ${bom.child_item_name || 'ì•Œ ìˆ˜ ì—†ëŠ” í’ˆëª©'}`,
      successMessage: 'BOM í•­ëª©ì´ ì„±ê³µì ìœ¼ë¡œ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.',
      errorMessage: 'BOM ì‚­ì œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.'
    });
  };

  const handleSaveBOM = async (bomData: Omit<BOM, 'bom_id' | 'is_active' | 'level'>) => {
    try {
      const method = editingBOM ? 'PUT' : 'POST';
      const body = editingBOM
        ? { ...bomData, bom_id: editingBOM.bom_id }
        : bomData;

      const response = await fetch('/api/bom', {
        method,
        headers: {
          'Content-Type': 'application/json; charset=utf-8',
        },
        body: JSON.stringify(body),
      });

      if (response.ok) {
        const successMessage = editingBOM ? 'BOMì´ ì„±ê³µì ìœ¼ë¡œ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤.' : 'BOMì´ ì„±ê³µì ìœ¼ë¡œ ë“±ë¡ë˜ì—ˆìŠµë‹ˆë‹¤.';
        success(editingBOM ? 'BOM ìˆ˜ì • ì™„ë£Œ' : 'BOM ë“±ë¡ ì™„ë£Œ', successMessage);
        setShowAddModal(false);
        setEditingBOM(null);
        fetchBOMData();
      } else {
        const errorData = await response.json();
        error('ì €ì¥ ì‹¤íŒ¨', errorData.error || 'ì €ì¥ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      }
    } catch (err) {
      console.error('Failed to save BOM:', err);
      error('ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜', 'ì„œë²„ì™€ì˜ ì—°ê²°ì— ë¬¸ì œê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  const handleCloseModal = () => {
    setShowAddModal(false);
