  };

  const handleCopyBOM = (bom: BOM) => {
    const confirmCopy = confirm(`${bom.parent_item_name || 'ì•Œ ìˆ˜ ì—†ëŠ” í’ˆëª©'}ì˜ BOM êµ¬ì¡°ë¥¼ ë³µì‚¬í•˜ì‹œê² ìŠµë‹ˆê¹Œ?`);
