    // ì§€ê¸‰ ë²ˆí˜¸ ìƒì„±
    await client.query(`
      CREATE OR REPLACE FUNCTION generate_payment_no()
      RETURNS VARCHAR AS $$
      DECLARE
        v_date VARCHAR(8);
        v_seq INTEGER;
        v_no VARCHAR(50);
      BEGIN
        v_date := TO_CHAR(CURRENT_DATE, 'YYYYMMDD');

        SELECT COALESCE(MAX(CAST(SUBSTRING(payment_no FROM 12) AS INTEGER)), 0) + 1
        INTO v_seq
        FROM payment_transactions
        WHERE payment_no LIKE 'PM-' || v_date || '-%';

        v_no := 'PM-' || v_date || '-' || LPAD(v_seq::TEXT, 4, '0');

        RETURN v_no;
      END;
      $$ LANGUAGE plpgsql;
    `);
    console.log('âœ… All transaction number generators created\n');

    // 7. RLS ì •ì±… ì„¤ì •
    console.log('ğŸ“¦ Setting up RLS policies...');

    // RLS í™œì„±í™”
    await client.query(`ALTER TABLE sales_transactions ENABLE ROW LEVEL SECURITY`);
    await client.query(`ALTER TABLE purchase_transactions ENABLE ROW LEVEL SECURITY`);
    await client.query(`ALTER TABLE collection_transactions ENABLE ROW LEVEL SECURITY`);
    await client.query(`ALTER TABLE payment_transactions ENABLE ROW LEVEL SECURITY`);

    // ê¸°ë³¸ ì •ì±… ìƒì„± (í™œì„± ë ˆì½”ë“œë§Œ ì¡°íšŒ)
    await client.query(`
      CREATE POLICY IF NOT EXISTS "View active sales" ON sales_transactions
        FOR SELECT USING (is_active = true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "View active purchases" ON purchase_transactions
        FOR SELECT USING (is_active = true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "View active collections" ON collection_transactions
        FOR SELECT USING (is_active = true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "View active payments" ON payment_transactions
        FOR SELECT USING (is_active = true)
    `);

    // INSERT/UPDATE ì •ì±… (ì„ì‹œë¡œ ëª¨ë“  ì‚¬ìš©ì í—ˆìš©)
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can insert sales" ON sales_transactions
        FOR INSERT WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can update sales" ON sales_transactions
        FOR UPDATE USING (true) WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can insert purchases" ON purchase_transactions
        FOR INSERT WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can update purchases" ON purchase_transactions
        FOR UPDATE USING (true) WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can insert collections" ON collection_transactions
        FOR INSERT WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can update collections" ON collection_transactions
        FOR UPDATE USING (true) WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can insert payments" ON payment_transactions
        FOR INSERT WITH CHECK (true)
    `);
    await client.query(`
      CREATE POLICY IF NOT EXISTS "Anyone can update payments" ON payment_transactions
        FOR UPDATE USING (true) WITH CHECK (true)
    `);
    console.log('âœ… All RLS policies created\n');

    // 8. ë·° ìƒì„±
    console.log('ğŸ“¦ Creating views...');
    await client.query(`
      CREATE OR REPLACE VIEW v_sales_summary AS
      SELECT
        s.transaction_id,
        s.transaction_date,
        s.transaction_no,
        c.company_name as customer_name,
        i.item_name,
        i.spec,
        s.quantity,
        s.unit_price,
        s.total_amount,
        s.payment_status,
        s.balance_amount
      FROM sales_transactions s
        LEFT JOIN companies c ON s.customer_id = c.company_id
        LEFT JOIN items i ON s.item_id = i.item_id
      WHERE s.is_active = true
      ORDER BY s.transaction_date DESC, s.transaction_no DESC
    `);

    await client.query(`
      CREATE OR REPLACE VIEW v_purchase_summary AS
      SELECT
        p.transaction_id,
        p.transaction_date,
        p.transaction_no,
        c.company_name as supplier_name,
        i.item_name,
        i.spec,
        p.quantity,
        p.unit_price,
        p.total_amount,
        p.payment_status,
        p.balance_amount
      FROM purchase_transactions p
        LEFT JOIN companies c ON p.supplier_id = c.company_id
        LEFT JOIN items i ON p.item_id = i.item_id
      WHERE p.is_active = true
      ORDER BY p.transaction_date DESC, p.transaction_no DESC
    `);
    console.log('âœ… All views created\n');

    console.log('ğŸ‰ Phase 1 ë°ì´í„°ë² ì´ìŠ¤ ìŠ¤í‚¤ë§ˆ ìƒì„± ì™„ë£Œ!\n');
    console.log('Created tables:');
    console.log('  - sales_transactions');
    console.log('  - purchase_transactions');
    console.log('  - collection_transactions');
    console.log('  - payment_transactions');
    console.log('\nNext step: API endpoints implementation');

  } catch (error) {
    console.error('âŒ Error:', error.message);
    if (error.detail) {
      console.error('   Detail:', error.detail);
    }
    if (error.hint) {
      console.error('   Hint:', error.hint);
    }
  } finally {
    await client.end();
    console.log('\nğŸ“Œ Database connection closed');
  }
}

