'use client';

import React, { useState } from 'react';
import { Download, FileSpreadsheet, Loader2 } from 'lucide-react';

interface ExcelExportButtonProps {
  onExport: () => Promise<void> | void;
  data?: any[];
  disabled?: boolean;
  variant?: 'primary' | 'secondary' | 'outline';
  size?: 'sm' | 'md' | 'lg';
  children?: React.ReactNode;
  tooltip?: string;
  showIcon?: boolean;
  className?: string;
}

export const ExcelExportButton: React.FC<ExcelExportButtonProps> = ({
  onExport,
  data = [],
  disabled = false,
  variant = 'outline',
  size = 'md',
  children,
  tooltip = 'Excel íŒŒì¼ë¡œ ë‚´ë³´ë‚´ê¸°',
  showIcon = true,
  className = ''
}) => {
  const [isExporting, setIsExporting] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastType, setToastType] = useState<'success' | 'error'>('success');

  const showToastMessage = (message: string, type: 'success' | 'error') => {
    setToastMessage(message);
    setToastType(type);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  const handleExport = async () => {
    if (disabled || isExporting) return;

    // Check if there's data to export
    if (data.length === 0) {
      showToastMessage('ë‚´ë³´ë‚¼ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.', 'error');
      return;
    }

    setIsExporting(true);

    try {
      await onExport();
      showToastMessage(
        `${data.length}ê°œ í•­ëª©ì´ Excel íŒŒì¼ë¡œ ë‚´ë³´ë‚´ê¸° ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤.`,
        'success'
      );
    } catch (error) {
      console.error('Excel export failed:', error);
      showToastMessage(
        'Excel ë‚´ë³´ë‚´ê¸° ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.',
        'error'
      );
    } finally {
      setIsExporting(false);
    }
  };

  // Style variants
  const baseStyles = 'inline-flex items-center justify-center gap-2 font-medium rounded-lg transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2';

  const variantStyles = {
    primary: 'bg-green-600 text-white hover:bg-green-700 focus:ring-green-500 shadow-sm',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 focus:ring-gray-500 shadow-sm',
    outline: 'border border-green-600 text-green-600 hover:bg-green-50 focus:ring-green-500 hover:border-green-700'
  };

  const sizeStyles = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2 text-sm',
    lg: 'px-6 py-3 text-base'
  };

  const disabledStyles = 'opacity-50 cursor-not-allowed';

  const buttonClasses = [
    baseStyles,
    variantStyles[variant],
    sizeStyles[size],
    (disabled || isExporting) && disabledStyles,
    className
  ].filter(Boolean).join(' ');

  return (
    <>
      <button
        onClick={handleExport}
        disabled={disabled || isExporting}
        className={buttonClasses}
        title={tooltip}
        aria-label={tooltip}
      >
        {isExporting ? (
          <>
            <Loader2 className="w-4 h-4 animate-spin" />
            ë‚´ë³´ë‚´ëŠ” ì¤‘...
          </>
        ) : (
          <>
            {showIcon && (
              <FileSpreadsheet className="w-4 h-4" />
            )}
            {children || 'Excel ë‚´ë³´ë‚´ê¸°'}
          </>
        )}
      </button>

      {/* Toast Notification */}
      {showToast && (
        <div
          className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg transition-all duration-300 ${
            toastType === 'success'
              ? 'bg-green-500 text-white'
              : 'bg-red-500 text-white'
          }`}
          role="alert"
        >
          <div className="flex items-center gap-2">
            {toastType === 'success' ? (
              <Download className="w-5 h-5" />
            ) : (
              <div className="w-5 h-5 rounded-full bg-white/20 flex items-center justify-center">
                <span className="text-sm font-bold">!</span>
              </div>
            )}
            <span className="font-medium">{toastMessage}</span>
          </div>
        </div>
      )}
    </>
  );
};

