import { NextRequest, NextResponse } from 'next/server';
import { supabaseAdmin } from '@/lib/supabase';
import { z } from 'zod';

// ì§€ê¸‰ ìˆ˜ì • ìŠ¤í‚¤ë§ˆ
const PaymentUpdateSchema = z.object({
  paid_amount: z.number().positive('ì§€ê¸‰ ê¸ˆì•¡ì€ 0ë³´ë‹¤ ì»¤ì•¼ í•©ë‹ˆë‹¤').optional(),
  payment_date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/, 'ë‚ ì§œ í˜•ì‹: YYYY-MM-DD').optional(),
  payment_method: z.enum(['CASH', 'TRANSFER', 'CHECK', 'CARD']).optional(),
  notes: z.string().optional()
});

// GET: íŠ¹ì • ì§€ê¸‰ ì¡°íšŒ
export const GET = async (
  request: NextRequest,
  { params }: { params: { id: string } }
) => {
  try {
    const id = params.id;

    if (!id) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ IDê°€ í•„ìš”í•©ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Fetch payment with joins
    const { data, error } = await supabaseAdmin
      .from('payments')
      .select(`
        *,
        purchase_transaction:purchase_transactions!purchase_transaction_id(
          transaction_id,
          transaction_no,
          transaction_date,
          total_amount,
          payment_status
        ),
        supplier:companies!supplier_id(
          company_id,
          company_name,
          company_code
        )
      `)
      .eq('payment_id', id)
      .single();

    if (error || !data) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    return NextResponse.json({
      success: true,
      data
    });
  } catch (error) {
    console.error('Payment get error:', error);
    return NextResponse.json(
      { success: false, error: 'ì§€ê¸‰ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
    );
  }
};

// PUT: ì§€ê¸‰ ìˆ˜ì • (ê¸ˆì•¡, ë‚ ì§œ, ë©”ëª¨ë§Œ)
export const PUT = async (
  request: NextRequest,
  { params }: { params: { id: string } }
) => {
  try {
    const id = params.id;

    if (!id) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ IDê°€ í•„ìš”í•©ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Korean encoding
    const text = await request.text();
    const body = JSON.parse(text);

    // Validate update data
    const result = PaymentUpdateSchema.safeParse(body);
    if (!result.success) {
      const errorMessages = result.error.errors.map(err => err.message).join(', ');
      return NextResponse.json(
        { success: false, error: errorMessages || 'ì…ë ¥ ë°ì´í„°ê°€ ìœ íš¨í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Get original payment data
    const { data: originalPayment, error: fetchError } = await supabaseAdmin
      .from('payments')
      .select('payment_id, purchase_transaction_id, paid_amount')
      .eq('payment_id', id)
      .eq('is_active', true)
      .single();

    if (fetchError || !originalPayment) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // If amount is being changed, recalculate payment status
    let needsStatusUpdate = false;
    let newPaymentStatus: 'PENDING' | 'PARTIAL' | 'COMPLETED' = 'PENDING';

    if (body.paid_amount !== undefined && body.paid_amount !== originalPayment.paid_amount) {
      needsStatusUpdate = true;

      // Get purchase transaction details
      const { data: purchaseTx, error: purchaseError } = await supabaseAdmin
        .from('purchase_transactions')
        .select('total_amount')
        .eq('transaction_id', originalPayment.purchase_transaction_id)
        .single();

      if (purchaseError || !purchaseTx) {
        return NextResponse.json(
          { success: false, error: 'ë§¤ì… ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
          { status: 400 }
        );
      }

      // Calculate total paid (excluding current payment)
      const { data: otherPayments } = await supabaseAdmin
        .from('payments')
        .select('paid_amount')
        .eq('purchase_transaction_id', originalPayment.purchase_transaction_id)
        .eq('is_active', true)
        .neq('payment_id', id);

      const otherPaidAmount = otherPayments?.reduce(
        (sum, payment) => sum + (payment.paid_amount || 0),
        0
      ) || 0;

      const totalPaid = otherPaidAmount + body.paid_amount;
      const remaining = purchaseTx.total_amount - totalPaid;

      // Validate that new amount doesn't exceed total
      if (remaining < 0) {
        return NextResponse.json(
          {
            success: false,
            error: `ì§€ê¸‰ ê¸ˆì•¡ì´ ì”ì•¡ì„ ì´ˆê³¼í•©ë‹ˆë‹¤. ìµœëŒ€ ì§€ê¸‰ ê°€ëŠ¥ ê¸ˆì•¡: ${purchaseTx.total_amount - otherPaidAmount}ì›`
          },
          { status: 400 }
        );
      }

      // Determine new payment status
      if (remaining === 0) {
        newPaymentStatus = 'COMPLETED';
      } else if (remaining < purchaseTx.total_amount) {
        newPaymentStatus = 'PARTIAL';
      } else {
        newPaymentStatus = 'PENDING';
      }
    }

    // Update payment
    const { data: updatedPayment, error: updateError } = await supabaseAdmin
      .from('payments')
      .update({
        ...body,
        updated_at: new Date().toISOString()
      })
      .eq('payment_id', id)
      .select(`
        *,
        purchase_transaction:purchase_transactions!purchase_transaction_id(
          transaction_id,
          transaction_no,
          transaction_date,
          total_amount,
          payment_status
        ),
        supplier:companies!supplier_id(
          company_id,
          company_name,
          company_code
        )
      `)
      .single();

    if (updateError) {
      console.error('Payment update error:', updateError);
      return NextResponse.json(
        { success: false, error: updateError?.message || 'ì§€ê¸‰ ìˆ˜ì • ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    if (!updatedPayment) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // Update purchase transaction status if needed
    if (needsStatusUpdate) {
      // Calculate new paid_amount for purchase transaction
      const { data: allPayments } = await supabaseAdmin
        .from('payments')
        .select('paid_amount')
        .eq('purchase_transaction_id', originalPayment.purchase_transaction_id)
        .eq('is_active', true);

      const newTotalPaid = allPayments?.reduce(
        (sum, payment) => sum + (payment.paid_amount || 0),
        0
      ) || 0;

      const { error: statusError } = await supabaseAdmin
        .from('purchase_transactions')
        .update({
          payment_status: newPaymentStatus,
          paid_amount: newTotalPaid,
          updated_at: new Date().toISOString()
        })
        .eq('transaction_id', originalPayment.purchase_transaction_id);

      if (statusError) {
        console.error('Purchase transaction status update error:', statusError);
        // Continue anyway - payment is updated
      }
    }

    return NextResponse.json({
      success: true,
      data: updatedPayment,
      message: needsStatusUpdate
        ? 'ì§€ê¸‰ì´ ìˆ˜ì •ë˜ê³  ë§¤ì… ê±°ë˜ ìƒíƒœê°€ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤'
        : 'ì§€ê¸‰ì´ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Payment update error:', error);
    return NextResponse.json(
      { success: false, error: 'ì§€ê¸‰ ìˆ˜ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
    );
  }
};

// DELETE: ì§€ê¸‰ ì‚­ì œ (soft delete)
export const DELETE = async (
  request: NextRequest,
  { params }: { params: { id: string } }
) => {
  try {
    const id = params.id;

    if (!id) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ IDê°€ í•„ìš”í•©ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Get payment data for payment status recalculation
    const { data: payment, error: fetchError } = await supabaseAdmin
      .from('payments')
      .select('purchase_transaction_id, paid_amount')
      .eq('payment_id', id)
      .single();

    if (fetchError || !payment) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // Soft delete payment
    const { data, error } = await supabaseAdmin
      .from('payments')
      .update({
        is_active: false,
        updated_at: new Date().toISOString()
      })
      .eq('payment_id', id)
      .select()
      .single();

    if (error) {
      console.error('Payment delete error:', error);
      return NextResponse.json(
        { success: false, error: error.message },
        { status: 500 }
      );
    }

    if (!data) {
      return NextResponse.json(
        { success: false, error: 'ì§€ê¸‰ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // Recalculate payment status for purchase transaction
    const { data: purchaseTx } = await supabaseAdmin
      .from('purchase_transactions')
      .select('total_amount')
      .eq('transaction_id', payment.purchase_transaction_id)
      .single();

    if (purchaseTx) {
      const { data: remainingPayments } = await supabaseAdmin
        .from('payments')
        .select('paid_amount')
        .eq('purchase_transaction_id', payment.purchase_transaction_id)
        .eq('is_active', true);

      const totalPaid = remainingPayments?.reduce(
        (sum, payment) => sum + (payment.paid_amount || 0),
        0
      ) || 0;

      const remaining = purchaseTx.total_amount - totalPaid;

      let newPaymentStatus: 'PENDING' | 'PARTIAL' | 'COMPLETED';
      if (remaining === 0 && totalPaid > 0) {
        newPaymentStatus = 'COMPLETED';
      } else if (remaining > 0 && totalPaid > 0) {
        newPaymentStatus = 'PARTIAL';
      } else {
        newPaymentStatus = 'PENDING';
      }

      await supabaseAdmin
        .from('purchase_transactions')
        .update({
          payment_status: newPaymentStatus,
          paid_amount: totalPaid,
          updated_at: new Date().toISOString()
        })
        .eq('transaction_id', payment.purchase_transaction_id);
    }

    return NextResponse.json({
      success: true,
      message: 'ì§€ê¸‰ì´ ì‚­ì œë˜ê³  ë§¤ì… ê±°ë˜ ìƒíƒœê°€ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Payment delete error:', error);
    return NextResponse.json(
      { success: false, error: 'ì§€ê¸‰ ì‚­ì œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
    );
  }
};

