import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import bcrypt from 'bcryptjs';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { username, password } = body;

    if (!username || !password) {
      return NextResponse.json({
        success: false,
        error: 'ì‚¬ìš©ìëª…ê³¼ ë¹„ë°€ë²ˆí˜¸ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.'
      }, { status: 400 });
    }

    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // ì‚¬ìš©ì ì¡°íšŒ
    const { data: users, error } = await supabase
      .from('users')
      .select('user_id, username, password, name, email, role, is_active')
      .eq('username', username)
      .eq('is_active', true);

    if (error || !users || users.length === 0) {
      return NextResponse.json({
        success: false,
        error: 'ì‚¬ìš©ìëª… ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤.'
      }, { status: 401 });
    }

    const user = users[0];

    // ë¹„ë°€ë²ˆí˜¸ ê²€ì¦
    const isPasswordValid = await bcrypt.compare(password, user.password);

    if (!isPasswordValid) {
      return NextResponse.json({
        success: false,
        error: 'ì‚¬ìš©ìëª… ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤.'
      }, { status: 401 });
    }

    // JWT í† í° ìƒì„± (ê°„ë‹¨í•œ ë²„ì „)
    const token = Buffer.from(JSON.stringify({
      userId: user.user_id,
      username: user.username,
      role: user.role,
      exp: Date.now() + 24 * 60 * 60 * 1000 // 24ì‹œê°„
    })).toString('base64');

    const userResponse = {
      username: user.username,
      name: user.name,
      email: user.email,
      role: user.role
    };

    const response = NextResponse.json({
      success: true,
      data: { user: userResponse, token },
      message: 'ë¡œê·¸ì¸ì— ì„±ê³µí–ˆìŠµë‹ˆë‹¤'
    });

    // HTTP-only ì¿ í‚¤ì— í† í° ì €ì¥
    response.cookies.set('auth_token', token, {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'strict',
      maxAge: 24 * 60 * 60 * 1000, // 24ì‹œê°„
      path: '/'
    });

    return response;
  } catch (error) {
    console.error('Login error:', error);
    return NextResponse.json({
      success: false,
      error: 'ë¡œê·¸ì¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
    }, { status: 500 });
  }
}

