'use client';

import { useState, useEffect, useRef } from 'react';
import { Database } from '@/types/supabase';

// Company type from unified Supabase layer
type Company = Database['public']['Tables']['companies']['Row'];

interface CompanySelectProps {
  value?: number | null;
  onChange: (companyId: number | null, company?: Company | undefined) => void;
  companyType?: 'SUPPLIER' | 'CUSTOMER' | 'OTHER';
  placeholder?: string;
  disabled?: boolean;
  required?: boolean;
  error?: string;
}

export default function CompanySelect({
  value,
  onChange,
  companyType,
  placeholder = "ê±°ë˜ì²˜ë¥¼ ì„ íƒí•˜ì„¸ìš”",
  disabled = false,
  required = false,
  error
}: CompanySelectProps) {
  const [companies, setCompanies] = useState<Company[]>([]);
  const [search, setSearch] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState<Company | null>(null);

  const searchInputRef = useRef<HTMLInputElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // ì™¸ë¶€ í´ë¦­ìœ¼ë¡œ ë“œë¡­ë‹¤ìš´ ë‹«ê¸°
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'Escape' && isOpen) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleKeyDown);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [isOpen]);

  // ê±°ë˜ì²˜ ëª©ë¡ ì¡°íšŒ
  const fetchCompanies = async (searchTerm: string = '') => {
    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (companyType) {
        params.append('type', companyType);
      }
      if (searchTerm) {
        params.append('search', searchTerm);
      }

      const response = await fetch(`/api/companies?${params.toString()}`);
      const result = await response.json();

      if (result.success) {
        setCompanies(result.data.data);
      } else {
        console.error('Failed to fetch companies:', result.error);
        setCompanies([]);
      }
    } catch (error) {
      console.error('Error fetching companies:', error);
      setCompanies([]);
    } finally {
      setLoading(false);
    }
  };

  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸ ì‹œ ì´ˆê¸° ë°ì´í„° ë¡œë“œ
  useEffect(() => {
    fetchCompanies();
  }, [companyType]);

  // ê²€ìƒ‰ì–´ ë³€ê²½ ì‹œ ê±°ë˜ì²˜ ëª©ë¡ ì¬ì¡°íšŒ
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (isOpen) {
        fetchCompanies(search);
      }
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [search, isOpen]);

  // ì„ íƒëœ ê°’ ë³€ê²½ ì‹œ í•´ë‹¹ ê±°ë˜ì²˜ ì •ë³´ ì„¤ì •
  useEffect(() => {
    if (value && companies.length > 0) {
      const company = companies.find(c => c.company_id === value);
      setSelectedCompany(company || null);
    } else {
      setSelectedCompany(null);
    }
  }, [value, companies]);

  const handleSelect = (company: Company) => {
    setSelectedCompany(company);
    onChange(company.company_id, company);
    setIsOpen(false);
    setSearch('');
  };

  const handleClear = () => {
    setSelectedCompany(null);
    onChange(null);
    setSearch('');
  };

  const handleToggle = () => {
    if (disabled) return;

    setIsOpen(!isOpen);
    if (!isOpen) {
      // ë“œë¡­ë‹¤ìš´ ì—´ ë•Œ ê²€ìƒ‰ ì…ë ¥ì°½ì— í¬ì»¤ìŠ¤
      setTimeout(() => {
        searchInputRef.current?.focus();
      }, 100);
    }
  };

  const filteredCompanies = companies.filter(company =>
    company.company_name.toLowerCase().includes(search.toLowerCase()) ||
    (company.business_registration_no && company.business_registration_no.includes(search)) ||
    (company.contact_person && company.contact_person.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <div className="relative" ref={dropdownRef}>
      {/* ì„ íƒëœ ê±°ë˜ì²˜ í‘œì‹œ / ë“œë¡­ë‹¤ìš´ í† ê¸€ ë²„íŠ¼ */}
      <div
        className={`
          min-h-[40px] px-3 py-2 border rounded-md cursor-pointer
          flex items-center justify-between
          ${disabled
            ? 'bg-gray-100 cursor-not-allowed'
            : 'bg-white hover:border-gray-400'
          }
          ${error
            ? 'border-red-500'
            : isOpen
              ? 'border-blue-500 ring-1 ring-blue-500'
              : 'border-gray-300'
          }
        `}
        onClick={handleToggle}
      >
        <div className="flex-1 min-w-0">
          {selectedCompany ? (
            <div className="flex items-center justify-between">
              <div className="truncate">
                <span className="font-medium">{selectedCompany.company_name}</span>
                {selectedCompany.business_registration_no && (
                  <span className="text-sm text-gray-500 ml-2">
                    ({selectedCompany.business_registration_no})
                  </span>
                )}
              </div>
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  handleClear();
                }}
                className="ml-2 text-gray-400 hover:text-gray-600"
                disabled={disabled}
              >
                âœ•
              </button>
            </div>
          ) : (
            <span className="text-gray-500">{placeholder}</span>
          )}
        </div>

        <div className="ml-2 text-gray-400">
          {isOpen ? 'â–²' : 'â–¼'}
        </div>
      </div>

      {/* ì—ëŸ¬ ë©”ì‹œì§€ */}
      {error && (
        <p className="mt-1 text-sm text-red-500">{error}</p>
      )}

      {/* ë“œë¡­ë‹¤ìš´ ëª©ë¡ */}
      {isOpen && (
        <div className="absolute z-[9999] w-full mt-1 bg-white dark:bg-gray-800 border border-gray-300 dark:border-gray-700 rounded-md shadow-lg max-h-60 overflow-hidden">
          {/* ê²€ìƒ‰ ì…ë ¥ì°½ */}
          <div className="p-2 border-b border-gray-200 dark:border-gray-600">
            <input
              ref={searchInputRef}
              type="text"
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-1 focus:ring-blue-500"
              placeholder="ê±°ë˜ì²˜ëª…, ì‚¬ì—…ìë²ˆí˜¸, ë‹´ë‹¹ìë¡œ ê²€ìƒ‰..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Escape') {
                  setIsOpen(false);
                }
              }}
            />
          </div>

          {/* ê±°ë˜ì²˜ ëª©ë¡ */}
          <div className="max-h-48 overflow-y-auto">
            {loading ? (
              <div className="p-3 text-center text-gray-500 dark:text-gray-400">ê²€ìƒ‰ ì¤‘...</div>
            ) : filteredCompanies.length > 0 ? (
              filteredCompanies.map((company) => (
                <div
                  key={company.company_id}
                  className="px-3 py-2 cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-700 border-b border-gray-100 dark:border-gray-600 last:border-b-0"
                  onMouseDown={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    handleSelect(company);
                  }}
                >
                  <div className="font-medium text-gray-900 dark:text-white">{company.company_name}</div>
                  <div className="text-sm text-gray-600 dark:text-gray-400 flex items-center gap-2">
                    {company.business_registration_no && (
                      <span>ì‚¬ì—…ì: {company.business_registration_no}</span>
                    )}
                    {company.contact_person && (
                      <span>ë‹´ë‹¹ì: {company.contact_person}</span>
                    )}
                    {company.phone && (
                      <span>ì „í™”: {company.phone}</span>
                    )}
                  </div>
                  {company.company_type && (
                    <div className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                      <span className={`
                        px-2 py-0.5 rounded-full text-xs
                        ${company.company_type === 'ê³µê¸‰ì‚¬'
                          ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'
                          : company.company_type === 'ê³ ê°ì‚¬'
                          ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
                          : 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200'
                        }
                      `}>
                        {company.company_type === 'ê³µê¸‰ì‚¬' ? 'ê³µê¸‰ì—…ì²´' :
                         company.company_type === 'ê³ ê°ì‚¬' ? 'ê³ ê°ì‚¬' : 'ê¸°íƒ€'}
                      </span>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <div className="p-3 text-center text-gray-500 dark:text-gray-400">
                {search ? 'ê²€ìƒ‰ ê²°ê³¼ê°€ ì—†ìŠµë‹ˆë‹¤.' : 'ë“±ë¡ëœ ê±°ë˜ì²˜ê°€ ì—†ìŠµë‹ˆë‹¤.'}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

