import { NextRequest, NextResponse } from 'next/server';
import { supabaseAdmin } from '@/lib/db-unified';
import { protectRoute } from '@/lib/middleware';
import {
  explodeBom,
  getBomTree,
  calculateTotalCost,
  getBomLevelSummary,
  getWhereUsed,
  validateBom,
  type BOMNode
} from '@/lib/bom';

interface TreeStats {
  totalItems: number;
  totalCost: number;
  maxLevel: number;
}

/**
 * GET /api/bom/explode
 * BOM ì „ê°œ ë° ê´€ë ¨ ì •ë³´ ì¡°íšŒ
 *
 * Query Parameters:
 * - parent_item_id: ìƒìœ„ í’ˆëª© ID (í•„ìˆ˜)
 * - type: ì¡°íšŒ ìœ í˜• (explode, tree, cost, summary, where-used, validate)
 * - max_level: ìµœëŒ€ ì „ê°œ ë ˆë²¨ (ê¸°ë³¸ê°’: 10)
 * - include_inactive: ë¹„í™œì„± í•­ëª© í¬í•¨ ì—¬ë¶€ (ê¸°ë³¸ê°’: false)
 */
export const GET = protectRoute(
  async (request: NextRequest, _user) => {
    try {
      const searchParams = request.nextUrl.searchParams;
      const parentItemId = searchParams.get('parent_item_id');
      const childItemId = searchParams.get('child_item_id');
      const type = searchParams.get('type') || 'explode';
      const maxLevel = parseInt(searchParams.get('max_level') || '10');
      const includeInactive = searchParams.get('include_inactive') === 'true';

      // WHERE-USED ì¡°íšŒì˜ ê²½ìš° child_item_id ì‚¬ìš©
      if (type === 'where-used') {
        if (!childItemId) {
          return NextResponse.json({
            success: false,
            error: 'child_item_idê°€ í•„ìš”í•©ë‹ˆë‹¤'
          }, { status: 400 });
        }

        const whereUsedData = await getWhereUsed(null, parseInt(childItemId));

        return NextResponse.json({
          success: true,
          data: whereUsedData
        });
      }

      // ë‚˜ë¨¸ì§€ ì¡°íšŒ ìœ í˜•ì€ parent_item_id í•„ìš”
      if (!parentItemId) {
        return NextResponse.json({
          success: false,
          error: 'parent_item_idê°€ í•„ìš”í•©ë‹ˆë‹¤'
        }, { status: 400 });
      }

      const itemId = parseInt(parentItemId);

      switch (type) {
        case 'explode': {
          // BOM ì „ê°œ (í‰ë©´ êµ¬ì¡°)
          const explodedBom = await explodeBom(null, itemId, 0, maxLevel, 1);

          // í‰ë©´ êµ¬ì¡°ë¡œ ë³€í™˜ (íŠ¸ë¦¬ë¥¼ í”Œë«í•˜ê²Œ)
          const flattenBom = (nodes: BOMNode[], result: BOMNode[] = []): BOMNode[] => {
            for (const node of nodes) {
              const { children, ...nodeData } = node;
              result.push(nodeData);
              if (children && children.length > 0) {
                flattenBom(children, result);
              }
            }
            return result;
          };

          const flatData = flattenBom(explodedBom);

          return NextResponse.json({
            success: true,
            data: flatData,
            summary: {
              total_items: flatData.length,
              max_level: Math.max(...flatData.map(item => item.level), 0),
              total_cost: flatData.reduce((sum, item) => sum + (item.total_price || 0), 0)
            }
          });
        }

        case 'tree': {
          // BOM íŠ¸ë¦¬ êµ¬ì¡°
          const bomTree = await getBomTree(null, itemId, includeInactive);

          if (!bomTree) {
            return NextResponse.json({
              success: false,
              error: 'í’ˆëª©ì„ ì°¾ì„ ìˆ˜ ì—†ê±°ë‚˜ BOMì´ ì¡´ì¬í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤'
            }, { status: 404 });
          }

          // íŠ¸ë¦¬ í†µê³„ ê³„ì‚°
          const calculateTreeStats = (node: BOMNode): TreeStats => {
            let totalItems = 1;
            let totalCost = node.total_price || 0;
            let maxLevel = node.level || 0;

            if (node.children && node.children.length > 0) {
              for (const child of node.children) {
                const childStats = calculateTreeStats(child);
                totalItems += childStats.totalItems;
                totalCost += childStats.totalCost;
                maxLevel = Math.max(maxLevel, childStats.maxLevel);
              }
            }

            return { totalItems, totalCost, maxLevel };
          };

          const stats = calculateTreeStats(bomTree);

          return NextResponse.json({
            success: true,
            data: bomTree,
            summary: {
              total_items: stats.totalItems,
              max_level: stats.maxLevel,
              total_cost: stats.totalCost
            }
          });
        }

        case 'cost': {
          // ì´ ì›ê°€ ê³„ì‚°
          const totalCost = await calculateTotalCost(null, itemId);

          // ìƒìœ„ í’ˆëª© ì •ë³´ ì¡°íšŒ
          const { data: itemData, error: itemError } = await supabaseAdmin
            .from('items')
            .select('item_code, item_name, spec, price')
            .eq('item_id', itemId)
            .single();

          if (itemError || !itemData) {
            return NextResponse.json({
              success: false,
              error: 'í’ˆëª©ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤'
            }, { status: 404 });
          }

          return NextResponse.json({
            success: true,
            data: {
              item: itemData,
              material_cost: totalCost,
              item_price: itemData.price || 0,
              margin: (itemData.price || 0) - totalCost,
              margin_rate: (itemData.price || 0) > 0
                ? (((itemData.price || 0) - totalCost) / (itemData.price || 0) * 100).toFixed(2) + '%'
                : '0%'
            }
          });
        }

        case 'summary': {
          // ë ˆë²¨ë³„ ìš”ì•½
          const levelSummary = await getBomLevelSummary(null, itemId);

          const totalSummary = levelSummary.reduce((acc, level) => ({
            total_items: acc.total_items + level.item_count,
            total_quantity: acc.total_quantity + level.total_quantity,
            total_cost: acc.total_cost + level.level_cost
          }), {
            total_items: 0,
            total_quantity: 0,
            total_cost: 0
          });

          return NextResponse.json({
            success: true,
            data: {
              levels: levelSummary,
              total: totalSummary
            }
          });
        }

        case 'validate': {
          // BOM ìœ íš¨ì„± ê²€ì‚¬
          const validation = await validateBom(null, itemId);

          return NextResponse.json({
            success: true,
            data: validation
          });
        }

        default: {
          return NextResponse.json({
            success: false,
            error: `ì§€ì›í•˜ì§€ ì•ŠëŠ” ì¡°íšŒ ìœ í˜•ì…ë‹ˆë‹¤: ${type}`
          }, { status: 400 });
        }
      }

    } catch (error) {
      console.error('Error in BOM explode:', error);
      return NextResponse.json(
        {
          success: false,
          error: 'BOM ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤'
        },
        { status: 500 }
      );
    }
  },
  { resource: 'production', action: 'read' }
);

/**
 * POST /api/bom/explode
 * BOM ì¼ê´„ ì „ê°œ (ì—¬ëŸ¬ í’ˆëª© ë™ì‹œ ì¡°íšŒ)
 */
export const POST = protectRoute(
  async (request: NextRequest, _user) => {
    try {
      const text = await request.text();
      const body = JSON.parse(text);
      const { item_ids, type = 'explode', max_level = 10 } = body;

      if (!item_ids || !Array.isArray(item_ids) || item_ids.length === 0) {
        return NextResponse.json({
          success: false,
          error: 'item_ids ë°°ì—´ì´ í•„ìš”í•©ë‹ˆë‹¤'
        }, { status: 400 });
      }

      const results = [];

      for (const itemId of item_ids) {
        try {
          let itemResult;

          switch (type) {
            case 'explode': {
              const explodedBom = await explodeBom(null, itemId, 0, max_level, 1);
              itemResult = {
                item_id: itemId,
                success: true,
                data: explodedBom
              };
              break;
            }

            case 'cost': {
              const totalCost = await calculateTotalCost(null, itemId);
              itemResult = {
                item_id: itemId,
                success: true,
                cost: totalCost
              };
              break;
            }

            case 'validate': {
              const validation = await validateBom(null, itemId);
              itemResult = {
                item_id: itemId,
                success: true,
                validation
              };
              break;
            }

            default: {
              itemResult = {
                item_id: itemId,
                success: false,
                error: `ì§€ì›í•˜ì§€ ì•ŠëŠ” ì¡°íšŒ ìœ í˜•: ${type}`
              };
            }
          }

          results.push(itemResult);

        } catch (error) {
          results.push({
            item_id: itemId,
            success: false,
            error: `ì²˜ë¦¬ ì‹¤íŒ¨: ${error}`
          });
        }
      }

      const successCount = results.filter(r => r.success).length;
      const failCount = results.length - successCount;

      return NextResponse.json({
        success: true,
        summary: {
          total: results.length,
          success: successCount,
          failed: failCount
        },
        data: results
      });

    } catch (error) {
      console.error('Error in batch BOM explode:', error);
      return NextResponse.json(
        {
          success: false,
          error: 'ì¼ê´„ BOM ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤'
        },
        { status: 500 }
      );
    }
  },
  { resource: 'production', action: 'read' }
);

