      unit: 'ê°œ'
    }
  ];

  const formatChangeValue = (change: number, isPercentage: boolean = false) => {
    if (change === 0) return '0';

    const absChange = Math.abs(change);
    if (isPercentage) {
      return `${change > 0 ? '+' : ''}${change.toFixed(1)}%`;
    } else {
      return `${change > 0 ? '+' : ''}${formatKoreanNumber(absChange)}`;
