import { NextRequest, NextResponse } from 'next/server';
import * as XLSX from 'xlsx';
import { getSupabaseClient } from '@/lib/db-unified';
import { IncomingForm } from 'formidable';
import { Readable } from 'stream';
import {
  convertExcelData,
  inventoryMapping,
  validateData,
  mapTransactionType,
  mapCompanyType
} from '@/lib/import-map';

// Disable body parsing for file upload
export const dynamic = 'force-dynamic';

export async function POST(request: NextRequest) {
  try {
    // Parse multipart form data
    const formData = await request.formData();
    const file = formData.get('file') as File;

    if (!file) {
      return NextResponse.json({
        success: false,
        error: 'íŒŒì¼ì´ ì—†ìŠµë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // Check file type
    if (!file.name.endsWith('.xlsx') && !file.name.endsWith('.xls')) {
      return NextResponse.json({
        success: false,
        error: 'Excel íŒŒì¼ë§Œ ì—…ë¡œë“œ ê°€ëŠ¥í•©ë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // Convert file to buffer
    const arrayBuffer = await file.arrayBuffer();
    const buffer = Buffer.from(arrayBuffer);

    // Parse Excel file
    const workbook = XLSX.read(buffer, { type: 'buffer' });
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];

    // Convert to JSON
    const rawData = XLSX.utils.sheet_to_json(worksheet);

    if (!rawData || rawData.length === 0) {
      return NextResponse.json({
        success: false,
        error: 'íŒŒì¼ì— ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.'
      }, { status: 400 });
    }

    // Convert Excel data using mapping
    let convertedData: Record<string, any>[];
    try {
      convertedData = convertExcelData(rawData as Record<string, any>[], inventoryMapping);
    } catch (error: unknown) {
      return NextResponse.json({
        success: false,
        error: `ë°ì´í„° ë³€í™˜ ì˜¤ë¥˜: ${error instanceof Error ? error.message : String(error)}`
      }, { status: 400 });
    }

    // Validate data
    const validation = validateData(convertedData as Record<string, any>[], inventoryMapping);
    if (!validation.isValid) {
      return NextResponse.json({
        success: false,
        error: 'ë°ì´í„° ìœ íš¨ì„± ê²€ì‚¬ ì‹¤íŒ¨',
        details: validation.errors
      }, { status: 400 });
    }

    // Process data and insert into database using Supabase
    const supabase = getSupabaseClient();
    const insertedRecords = [];
    const errors = [];

    for (let i = 0; i < convertedData.length; i++) {
      const row = convertedData[i] as Record<string, any>;
      try {
        // Map transaction type to English
        row.transaction_type = mapTransactionType(row.transaction_type);

        // Get item_id from item_code
        const { data: items, error: itemError } = await supabase
          .from('items')
          .select('item_id, current_stock')
          .eq('item_code', row.item_code)
          .eq('is_active', true)
          .single();

        if (itemError || !items) {
          errors.push(`í–‰ ${i + 1}: í’ˆëª©ì½”ë“œ '${row.item_code}'ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`);
          continue;
        }

        const itemId = items.item_id;

        // Get company_id from company_code (if provided)
        let companyId = null;
        if (row.company_code) {
          const { data: companies, error: companyError } = await supabase
            .from('companies')
            .select('company_id')
            .eq('company_code', row.company_code)
            .eq('is_active', true)
            .single();

          if (companyError || !companies) {
            errors.push(`í–‰ ${i + 1}: íšŒì‚¬ì½”ë“œ '${row.company_code}'ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.`);
            continue;
          }

          companyId = companies.company_id;
        }

        // Insert inventory transaction
        const { data: insertedTransaction, error: insertError } = await supabase
          .from('inventory_transactions')
          .insert({
            transaction_date: row.transaction_date,
            transaction_type: row.transaction_type,
            item_id: itemId,
            quantity: row.quantity,
            unit: row.unit,
            company_id: companyId,
            reference_number: row.reference_number || null,
            remarks: row.remarks || null,
            user_id: 1,
            created_at: new Date().toISOString()
          })
          .select('transaction_id')
          .single();

        if (insertError) {
          errors.push(`í–‰ ${i + 1}: ê±°ë˜ ì…ë ¥ ì‹¤íŒ¨ - ${insertError.message}`);
          continue;
        }

        // Update item stock based on transaction type
        let stockChange = 0;
        switch (row.transaction_type) {
          case 'RECEIVING':
          case 'PRODUCTION':
            stockChange = row.quantity;
            break;
          case 'SHIPPING':
            stockChange = -row.quantity;
            break;
        }

        if (stockChange !== 0) {
          const currentStock = items.current_stock ?? 0;
          const newStock = currentStock + stockChange;
          const { error: updateError } = await supabase
            .from('items')
            .update({ current_stock: newStock })
            .eq('item_id', itemId);

          if (updateError) {
            errors.push(`í–‰ ${i + 1}: ì¬ê³  ì—…ë°ì´íŠ¸ ì‹¤íŒ¨ - ${updateError.message}`);
            continue;
          }
        }

        insertedRecords.push({
          transaction_id: insertedTransaction?.transaction_id,
          row: i + 1,
          item_code: row.item_code,
          quantity: row.quantity,
          transaction_type: row.transaction_type
        });

      } catch (error: unknown) {
        errors.push(`í–‰ ${i + 1}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }

    const result = { insertedRecords, errors };

    return NextResponse.json({
      success: true,
      data: {
        totalProcessed: convertedData.length,
        successCount: result.insertedRecords.length,
        errorCount: result.errors.length,
        inserted: result.insertedRecords,
        errors: result.errors
      }
    });

  } catch (error) {
    console.error('Excel import error:', error);
    return NextResponse.json({
      success: false,
      error: 'Excel íŒŒì¼ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
    }, { status: 500 });
