      })
      .eq('transaction_id', transactionId)
      .eq('is_active', true)
      .select('transaction_id, transaction_no')
      .single();

    if (error) {
      throw new APIError('íŒë§¤ ê±°ë˜ë¥¼ ë¹„í™œì„±í™”í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.', 500, error.message);
    }

    if (!data) {
      throw new APIError('ì‚­ì œ ëŒ€ìƒ íŒë§¤ ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
    }

    return NextResponse.json({
      success: true,
      message: 'íŒë§¤ ê±°ë˜ê°€ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.',
      data: {
        transaction_id: data.transaction_id,
