/**
 * HTTP status code mapping for error codes
 */
export const ERROR_STATUS_CODES: Record<ErrorCode, number> = {
  // 400 Bad Request
  [ErrorCode.VALIDATION_ERROR]: 400,
  [ErrorCode.MISSING_REQUIRED_FIELD]: 400,
  [ErrorCode.INVALID_FORMAT]: 400,
  [ErrorCode.INVALID_TYPE]: 400,
  [ErrorCode.INVALID_VALUE]: 400,

  // 401 Unauthorized
  [ErrorCode.UNAUTHORIZED]: 401,
  [ErrorCode.INVALID_CREDENTIALS]: 401,
  [ErrorCode.TOKEN_EXPIRED]: 401,
  [ErrorCode.TOKEN_INVALID]: 401,

  // 403 Forbidden
  [ErrorCode.FORBIDDEN]: 403,
  [ErrorCode.INSUFFICIENT_PERMISSIONS]: 403,

  // 404 Not Found
  [ErrorCode.RESOURCE_NOT_FOUND]: 404,

  // 409 Conflict
  [ErrorCode.DUPLICATE_ENTRY]: 409,
  [ErrorCode.RESOURCE_CONFLICT]: 409,
  [ErrorCode.RESOURCE_LOCKED]: 409,

  // 422 Unprocessable Entity
  [ErrorCode.BUSINESS_RULE_VIOLATION]: 422,
  [ErrorCode.INSUFFICIENT_STOCK]: 422,
  [ErrorCode.INVALID_OPERATION]: 422,
  [ErrorCode.CONSTRAINT_VIOLATION]: 422,

  // 429 Too Many Requests
  [ErrorCode.RATE_LIMIT_EXCEEDED]: 429,
  [ErrorCode.TOO_MANY_REQUESTS]: 429,

  // 500 Internal Server Error
  [ErrorCode.INTERNAL_SERVER_ERROR]: 500,
  [ErrorCode.DATABASE_ERROR]: 500,
  [ErrorCode.CONFIGURATION_ERROR]: 500,

  // 502 Bad Gateway
  [ErrorCode.EXTERNAL_SERVICE_ERROR]: 502,

  // 503 Service Unavailable
  [ErrorCode.SERVICE_UNAVAILABLE]: 503,

  // 504 Gateway Timeout
  [ErrorCode.TIMEOUT_ERROR]: 504,
};

/**
 * Standardized error response interface
 */
export interface ErrorResponse {
  success: false;
  error: {
    code: ErrorCode;
    message: string;
    details?: any;
    field?: string;
    resource?: string;
    action?: string;
  };
  timestamp: string;
  requestId?: string;
  path?: string;
}

/**
 * Standardized success response interface
 */
export interface SuccessResponse<T = any> {
  success: true;
  data: T;
  message?: string;
  timestamp?: string;
  requestId?: string;
}

/**
 * API Response type union
 */
export type APIResponse<T = any> = SuccessResponse<T> | ErrorResponse;

/**
 * Error details for specific error types
 */
export interface ValidationErrorDetails {
  field: string;
  value?: any;
  expectedType?: string;
  allowedValues?: any[];
  constraints?: Record<string, any>;
}

export interface BusinessRuleErrorDetails {
  rule: string;
  entity?: string;
  entityId?: string | number;
  conflictingData?: any;
}

export interface ResourceErrorDetails {
  resource: string;
  id?: string | number;
  action?: string;
  availability?: Date;
}

export interface RateLimitErrorDetails {
  limit: number;
  windowMs: number;
  retryAfter: number;
  endpoint?: string;
}

/**
 * Create standardized error response
 */
export function createErrorResponse(
  code: ErrorCode,
  customMessage?: string,
  details?: any,
  options: {
    field?: string;
    resource?: string;
    action?: string;
    requestId?: string;
    path?: string;
  } = {}
): ErrorResponse {
  return {
    success: false,
    error: {
      code,
      message: customMessage || ERROR_MESSAGES[code],
      details,
      field: options.field,
      resource: options.resource,
      action: options.action,
    },
    timestamp: new Date().toISOString(),
    requestId: options.requestId,
    path: options.path,
  };
}

/**
 * Create standardized success response
 */
export function createSuccessResponse<T>(
  data: T,
  message?: string,
  options: {
    requestId?: string;
  } = {}
): SuccessResponse<T> {
  return {
    success: true,
    data,
    message,
    timestamp: new Date().toISOString(),
    requestId: options.requestId,
  };
}

/**
 * Get HTTP status code for error code
 */
export function getStatusCode(errorCode: ErrorCode): number {
  return ERROR_STATUS_CODES[errorCode] || 500;
}

/**
 * Check if error is client error (4xx)
 */
export function isClientError(errorCode: ErrorCode): boolean {
  const statusCode = getStatusCode(errorCode);
  return statusCode >= 400 && statusCode < 500;
}

/**
 * Check if error is server error (5xx)
 */
export function isServerError(errorCode: ErrorCode): boolean {
  const statusCode = getStatusCode(errorCode);
  return statusCode >= 500;
}

/**
 * ERP-specific error class
 */
export class ERPError extends Error {
  public readonly code: ErrorCode;
  public readonly details?: any;
  public readonly field?: string;
  public readonly resource?: string;
  public readonly action?: string;

  constructor(
    code: ErrorCode,
    message?: string,
    details?: any,
    options: {
      field?: string;
      resource?: string;
      action?: string;
    } = {}
  ) {
    super(message || ERROR_MESSAGES[code]);
    this.name = 'ERPError';
    this.code = code;
    this.details = details;
    this.field = options.field;
    this.resource = options.resource;
    this.action = options.action;

    // Maintains proper stack trace for where our error was thrown (only available on V8)
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, ERPError);
    }
  }

  /**
   * Get HTTP status code for this error
   */
  get statusCode(): number {
    return getStatusCode(this.code);
  }

  /**
   * Convert to error response format
   */
  toErrorResponse(options: {
    requestId?: string;
    path?: string;
  } = {}): ErrorResponse {
    return createErrorResponse(
      this.code,
      this.message,
      this.details,
      {
        field: this.field,
        resource: this.resource,
        action: this.action,
        requestId: options.requestId,
        path: options.path,
      }
    );
  }

  /**
   * Create validation error
   */
  static validation(
    message: string,
    field?: string,
    details?: ValidationErrorDetails
  ): ERPError {
    return new ERPError(
      ErrorCode.VALIDATION_ERROR,
      message,
      details,
      { field }
    );
  }

  /**
   * Create business rule error
   */
  static businessRule(
    message: string,
    details?: BusinessRuleErrorDetails
  ): ERPError {
    return new ERPError(
      ErrorCode.BUSINESS_RULE_VIOLATION,
      message,
      details
    );
  }

  /**
   * Create resource not found error
   */
  static notFound(
    resource: string,
    id?: string | number,
    action?: string
  ): ERPError {
    return new ERPError(
      ErrorCode.RESOURCE_NOT_FOUND,
      `${resource}ì„(ë¥¼) ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤`,
      { id },
      { resource, action }
    );
  }

  /**
   * Create duplicate entry error
   */
  static duplicate(
    resource: string,
    field?: string,
    value?: any
  ): ERPError {
    return new ERPError(
      ErrorCode.DUPLICATE_ENTRY,
      `ì¤‘ë³µëœ ${resource}ì…ë‹ˆë‹¤`,
      { field, value },
      { resource, field }
    );
  }

  /**
   * Create insufficient permissions error
   */
  static forbidden(
    action?: string,
    resource?: string
  ): ERPError {
    return new ERPError(
      ErrorCode.FORBIDDEN,
      'ì ‘ê·¼ ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤',
      { action, resource },
      { action, resource }
    );
  }

  /**
   * Create database error
   */
  static database(
    message: string,
    details?: any
  ): ERPError {
    return new ERPError(
      ErrorCode.DATABASE_ERROR,
      message,
      details
    );
  }
}

/**
 * MySQL error code mapping to ERP error codes
 */
export const MYSQL_ERROR_MAPPING: Record<string, ErrorCode> = {
  'ER_NO_SUCH_TABLE': ErrorCode.CONFIGURATION_ERROR,
  'ER_BAD_FIELD_ERROR': ErrorCode.CONFIGURATION_ERROR,
  'ER_DUP_ENTRY': ErrorCode.DUPLICATE_ENTRY,
  'ER_NO_REFERENCED_ROW': ErrorCode.CONSTRAINT_VIOLATION,
  'ER_ROW_IS_REFERENCED': ErrorCode.CONSTRAINT_VIOLATION,
  'ER_LOCK_WAIT_TIMEOUT': ErrorCode.RESOURCE_LOCKED,
  'ER_LOCK_DEADLOCK': ErrorCode.RESOURCE_CONFLICT,
  'ECONNREFUSED': ErrorCode.SERVICE_UNAVAILABLE,
  'ETIMEDOUT': ErrorCode.TIMEOUT_ERROR,
  'ENOTFOUND': ErrorCode.CONFIGURATION_ERROR,
};

/**
 * Convert MySQL error to ERP error
 */
export function convertMySQLError(mysqlError: any): ERPError {
  const errorCode = MYSQL_ERROR_MAPPING[mysqlError.code] || ErrorCode.DATABASE_ERROR;

  let message = ERROR_MESSAGES[errorCode];
  let details = mysqlError;

  // Customize messages for specific MySQL errors
  switch (mysqlError.code) {
    case 'ER_DUP_ENTRY':
      message = 'ì¤‘ë³µëœ ë°ì´í„°ì…ë‹ˆë‹¤';
      break;
    case 'ER_NO_REFERENCED_ROW':
      message = 'ì°¸ì¡°í•˜ëŠ” ë°ì´í„°ê°€ ì¡´ì¬í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤';
      break;
    case 'ER_ROW_IS_REFERENCED':
      message = 'ì‚­ì œí•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ë‹¤ë¥¸ ë°ì´í„°ì—ì„œ ì°¸ì¡°ì¤‘ì…ë‹ˆë‹¤';
      break;
    case 'ER_LOCK_WAIT_TIMEOUT':
      message = 'ë‹¤ë¥¸ ì‚¬ìš©ìê°€ ì‚¬ìš©ì¤‘ì…ë‹ˆë‹¤. ì ì‹œ í›„ ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”';
      break;
  }

  // Hide sensitive details in production
  if (process.env.NODE_ENV === 'production') {
    details = { code: mysqlError.code };
  }

  return new ERPError(errorCode, message, details);
}

