'use client';

import { useState, useCallback } from 'react';
import ConfirmModal, { ConfirmType } from '@/components/ConfirmModal';
import { useToast } from '@/contexts/ToastContext';

interface ConfirmOptions {
  title?: string;
  message?: string;
  confirmText?: string;
  cancelText?: string;
  type?: ConfirmType;
}

interface ConfirmState {
  isOpen: boolean;
  options: ConfirmOptions;
  resolve: ((value: boolean) => void) | null;
  loading: boolean;
}

export const useConfirm = () => {
  const [state, setState] = useState<ConfirmState>({
    isOpen: false,
    options: {},
    resolve: null,
    loading: false
  });
  const { success, error } = useToast();

  const confirm = useCallback(async (options: ConfirmOptions = {}): Promise<boolean> => {
    return new Promise((resolve) => {
      setState({
        isOpen: true,
        options,
        resolve,
        loading: false
      });
    });
  }, []);

  // Korean convenience methods
  const deleteConfirm = useCallback(async (
    title: string = 'ì‚­ì œ í™•ì¸',
    message: string = 'ì •ë§ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?'
  ): Promise<boolean> => {
    return confirm({
      title,
      message,
      type: 'delete',
      confirmText: 'ì‚­ì œ',
      cancelText: 'ì·¨ì†Œ'
    });
  }, [confirm]);

  const warningConfirm = useCallback(async (
    title: string = 'í™•ì¸ í•„ìš”',
    message: string = 'ì´ ì‘ì—…ì„ ì§„í–‰í•˜ì‹œê² ìŠµë‹ˆê¹Œ?'
  ): Promise<boolean> => {
    return confirm({
      title,
      message,
      type: 'warning',
      confirmText: 'í™•ì¸',
      cancelText: 'ì·¨ì†Œ'
    });
  }, [confirm]);

  const dangerConfirm = useCallback(async (
    title: string = 'ìœ„í—˜í•œ ì‘ì—…',
    message: string = 'ì´ ì‘ì—…ì„ ê³„ì†í•˜ì‹œê² ìŠµë‹ˆê¹Œ?'
  ): Promise<boolean> => {
    return confirm({
      title,
      message,
      type: 'danger',
      confirmText: 'ê³„ì†',
      cancelText: 'ì·¨ì†Œ'
    });
  }, [confirm]);

  // Enhanced delete confirm with automatic toast notifications
  const deleteWithToast = useCallback(async (
    deleteAction: () => Promise<void>,
    options: {
      title?: string;
      message?: string;
      itemName?: string;
      successMessage?: string;
      errorMessage?: string;
    } = {}
  ): Promise<boolean> => {
    const {
      title = 'ì‚­ì œ í™•ì¸',
      message = options.itemName
        ? `"${options.itemName}"ì„(ë¥¼) ì •ë§ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?`
        : 'ì •ë§ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?',
      successMessage = 'ì„±ê³µì ìœ¼ë¡œ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.',
      errorMessage = 'ì‚­ì œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.'
    } = options;

    try {
      const confirmed = await deleteConfirm(title, message);

      if (!confirmed) {
        return false;
      }

      setState(prev => ({ ...prev, loading: true }));

      await deleteAction();

      setState(prev => ({ ...prev, loading: false, isOpen: false }));
      success('ì‚­ì œ ì™„ë£Œ', successMessage);

      return true;
    } catch (err) {
      setState(prev => ({ ...prev, loading: false }));
      console.error('Delete action failed:', err);
      error('ì‚­ì œ ì‹¤íŒ¨', errorMessage);
      return false;
    }
  }, [deleteConfirm, success, error]);

  const handleConfirm = useCallback(() => {
    if (state.resolve && !state.loading) {
      state.resolve(true);
      setState({
        isOpen: false,
        options: {},
        resolve: null,
        loading: false
      });
    }
  }, [state.resolve, state.loading]);

  const handleCancel = useCallback(() => {
    if (state.resolve && !state.loading) {
      state.resolve(false);
      setState({
        isOpen: false,
        options: {},
        resolve: null,
        loading: false
      });
    }
  }, [state.resolve, state.loading]);

  const setLoading = useCallback((loading: boolean) => {
    setState(prev => ({ ...prev, loading }));
  }, []);

  const ConfirmDialog = useCallback(() => (
    <ConfirmModal
      isOpen={state.isOpen}
      onConfirm={handleConfirm}
      onCancel={handleCancel}
      type={state.options.type}
      title={state.options.title}
      message={state.options.message}
      confirmText={state.options.confirmText}
      cancelText={state.options.cancelText}
      loading={state.loading}
    />
  ), [state, handleConfirm, handleCancel]);

  return {
    confirm,
    deleteConfirm,
    warningConfirm,
    dangerConfirm,
    deleteWithToast,
    setLoading,
    ConfirmDialog
  };
};

export default useConfirm;

