}

export interface ShippingFormData {
  transaction_date: string;
  customer_id?: number;
  items: ShippingItem[];
  reference_no?: string;
  delivery_address?: string;
  delivery_date?: string;
  notes?: string;
  created_by: number;
}

// Search and dropdown interfaces
export interface SearchOption {
  id: number;
  code: string;
  name: string;
  specification?: string;
  unit?: string;
  current_stock?: number;
  unit_price?: number;
  business_type?: string;
}

// API response interfaces
export interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
}

export interface PaginationInfo {
  total: number;
  limit: number;
  offset: number;
  hasMore: boolean;
}

export interface TransactionListResponse {
  transactions: InventoryTransaction[];
  pagination?: PaginationInfo;
}

export interface StockListResponse {
  stocks: StockInfo[];
  pagination?: PaginationInfo;
}

// Form validation interfaces
export interface FormError {
  field: string;
  message: string;
}

export interface ValidationResult {
  isValid: boolean;
  errors: FormError[];
}

// Stock status enums and types
export type StockStatus = 'normal' | 'low' | 'empty' | 'excess';

export interface StockStatusInfo {
  status: StockStatus;
  message: string;
  icon: React.ComponentType<{ className?: string }>;
  color: string;
}

// Inventory transaction types
export const TRANSACTION_TYPES = {
  RECEIVING: 'ì…ê³ ',
  PRODUCTION_IN: 'ìƒì‚°ì…ê³ ',
  PRODUCTION_OUT: 'ìƒì‚°ì¶œê³ ',
  SHIPPING: 'ì¶œê³ '
} as const;

export type TransactionType = typeof TRANSACTION_TYPES[keyof typeof TRANSACTION_TYPES];

// Business types for companies
export const BUSINESS_TYPES = {
  SUPPLIER: 'ê³µê¸‰ì—…ì²´',
  CUSTOMER: 'ê³ ê°',
  BOTH: 'ë‘˜ë‹¤'
} as const;

export type BusinessType = typeof BUSINESS_TYPES[keyof typeof BUSINESS_TYPES];

// Form submission handlers
export type FormSubmitHandler<T> = (data: T) => Promise<void> | void;
export type FormCancelHandler = () => void;

// Common props for form components
export interface BaseFormProps<T> {
  onSubmit: FormSubmitHandler<T>;
  onCancel: FormCancelHandler;
  initialData?: Partial<T>;
  isEdit?: boolean;
  isLoading?: boolean;
}

// Props for specific form components (type aliases for clarity)
export type ReceivingFormProps = BaseFormProps<ReceivingFormData>;
export type ProductionFormProps = BaseFormProps<ProductionFormData>;
export type ShippingFormProps = BaseFormProps<ShippingFormData>;

// Tab configuration interface
export interface InventoryTab {
  id: 'receiving' | 'production' | 'shipping';
  label: string;
  icon: React.ComponentType<{ className?: string }>;
  description: string;
  color: string;
  bgColor: string;
}

// Filter and search interfaces
export interface InventoryFilters {
  start_date?: string;
  end_date?: string;
  item_id?: number;
  company_id?: number;
  transaction_type?: TransactionType;
  reference_no?: string;
  limit?: number;
  offset?: number;
}

export interface SearchFilters {
  query?: string;
  category?: string;
  is_active?: boolean;
  business_type?: BusinessType;
  limit?: number;
}

// Utility types
export type Optional<T, K extends keyof T> = Omit<T, K> & Partial<Pick<T, K>>;
export type RequiredFields<T, K extends keyof T> = T & Required<Pick<T, K>>;

