import { NextRequest, NextResponse } from 'next/server';
import { getSupabaseClient } from '@/lib/db-unified';
import { z } from 'zod';

// ë§¤ì… ê±°ë˜ ìƒì„± ìŠ¤í‚¤ë§ˆ
const PurchaseTransactionCreateSchema = z.object({
  transaction_date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/, 'ë‚ ì§œ í˜•ì‹: YYYY-MM-DD'),
  supplier_id: z.number().positive('ê³µê¸‰ì‚¬ IDëŠ” ì–‘ìˆ˜ì—¬ì•¼ í•©ë‹ˆë‹¤'),
  item_id: z.number().positive('í’ˆëª© IDëŠ” ì–‘ìˆ˜ì—¬ì•¼ í•©ë‹ˆë‹¤'),
  item_name: z.string().min(1, 'í’ˆëª©ëª…ì€ í•„ìˆ˜ì…ë‹ˆë‹¤'),
  spec: z.string().optional(),
  unit: z.string().optional(),
  // SCHEMA FIX: vehicle_model and material_type removed - columns not in Supabase yet
  // TODO: Re-enable when migration is applied
  // vehicle_model: z.string().optional(),
  // material_type: z.string().optional(),
  quantity: z.number().positive('ìˆ˜ëŸ‰ì€ 0ë³´ë‹¤ ì»¤ì•¼ í•©ë‹ˆë‹¤'),
  unit_price: z.number().min(0, 'ë‹¨ê°€ëŠ” 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  supply_amount: z.number().min(0, 'ê³µê¸‰ê°€ëŠ” 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  tax_amount: z.number().min(0, 'ì„¸ì•¡ì€ 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤').optional(),
  total_amount: z.number().min(0, 'ì´ì•¡ì€ 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  payment_status: z.enum(['PENDING', 'PARTIAL', 'COMPLETED']).optional(),
  payment_amount: z.number().min(0).optional(),
  balance_amount: z.number().min(0).optional(),
  notes: z.string().optional(),  // Changed from 'description' to match form field
  reference_no: z.string().optional()
});

// GET: ë§¤ì… ê±°ë˜ ëª©ë¡ ì¡°íšŒ
export const GET = async (request: NextRequest) => {
  try {
    const searchParams = request.nextUrl.searchParams;
    const page = Math.max(1, parseInt(searchParams.get('page') || '1', 10) || 1);
    const limit = Math.min(100, Math.max(1, parseInt(searchParams.get('limit') || '20', 10) || 20));
    const offset = (page - 1) * limit;

    const supplier_id = searchParams.get('supplier_id');
    const item_id = searchParams.get('item_id');
    const vehicle_model = searchParams.get('vehicle_model');
    const payment_status = searchParams.get('payment_status');
    const start_date = searchParams.get('start_date');
    const end_date = searchParams.get('end_date');
    const search = searchParams.get('search');
    const orderBy = searchParams.get('orderBy') || 'transaction_date';
    const order = searchParams.get('order') || 'desc';

    const supabase = getSupabaseClient();

    // Build query
    let query = supabase
      .from('purchase_transactions')
      .select(`
        *,
        supplier:companies!supplier_id(company_id, company_name, company_code),
        item:items!item_id(item_id, item_name, item_code)
      `, { count: 'exact' })
      .eq('is_active', true);

    // Apply filters
    if (supplier_id) {
      query = query.eq('supplier_id', supplier_id);
    }

    if (item_id) {
      query = query.eq('item_id', item_id);
    }

    // Note: vehicle_model filter temporarily disabled (column not in current schema)
    // if (vehicle_model) {
    //   query = query.ilike('vehicle_model', `%${vehicle_model}%`);
    // }

    if (payment_status) {
      query = query.eq('payment_status', payment_status);
    }

    if (start_date) {
      query = query.gte('transaction_date', start_date);
    }

    if (end_date) {
      query = query.lte('transaction_date', end_date);
    }

    if (search) {
      // Note: vehicle_model search temporarily removed (column not in current schema)
      query = query.or(`transaction_no.ilike.%${search}%,item_name.ilike.%${search}%`);
    }

    // Apply ordering and pagination
    query = query
      .order(orderBy, { ascending: order === 'asc' })
      .range(offset, offset + limit - 1);

    const { data, error, count } = await query;

    if (error) {
      console.error('Purchase list error:', error);
      return NextResponse.json(
        { success: false, error: error?.message || 'ë§¤ì… ê±°ë˜ ì¡°íšŒ ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      success: true,
      data: data || [],
      pagination: {
        page,
        limit,
        total: count || 0,
        totalPages: Math.ceil((count || 0) / limit)
      }
    });
  } catch (error) {
    console.error('Purchase list error:', error);
    return NextResponse.json(
      { success: false, error: 'ë§¤ì… ê±°ë˜ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
    );
  }
};

// POST: ë§¤ì… ê±°ë˜ ìƒì„± (ì¬ê³  ì¦ê°€ í¬í•¨)
export const POST = async (request: NextRequest) => {
  try {
    const text = await request.text();
    const body = JSON.parse(text);

    // Validate input
    const result = PurchaseTransactionCreateSchema.safeParse(body);
    if (!result.success) {
      const errorMessages = result.error.errors.map(err => err.message).join(', ');
      return NextResponse.json(
        { success: false, error: errorMessages || 'ì…ë ¥ ë°ì´í„°ê°€ ìœ íš¨í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    const supabase = getSupabaseClient();

    // Verify supplier exists and is type SUPPLIER or BOTH
    const { data: supplier, error: supplierError } = await supabase
      .from('companies')
      .select('company_id, company_type')
      .eq('company_id', result.data.supplier_id)
      .eq('is_active', true)
      .single();

    if (supplierError || !supplier) {
      return NextResponse.json(
        { success: false, error: 'ìœ íš¨í•˜ì§€ ì•Šì€ ê³µê¸‰ì‚¬ IDì…ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Check if supplier type is SUPPLIER or BOTH (ì–‘ë°©í–¥)
    const normalizedType = supplier.company_type === 'ê³µê¸‰ì‚¬' ? 'SUPPLIER' :
                          supplier.company_type === 'ì–‘ë°©í–¥' ? 'BOTH' :
                          supplier.company_type;

    if (normalizedType !== 'SUPPLIER' && normalizedType !== 'BOTH') {
      return NextResponse.json(
        { success: false, error: 'ì„ íƒí•œ ê±°ë˜ì²˜ëŠ” ê³µê¸‰ì‚¬ê°€ ì•„ë‹™ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Verify item exists
    const { data: item, error: itemError } = await supabase
      .from('items')
      .select('item_id, current_stock')
      .eq('item_id', result.data.item_id)
      .eq('is_active', true)
      .single();

    if (itemError || !item) {
      return NextResponse.json(
        { success: false, error: 'ìœ íš¨í•˜ì§€ ì•Šì€ í’ˆëª© IDì…ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    // Generate transaction number
    const { data: transactionNo, error: genError } = await supabase
      .rpc('generate_purchase_no');

    if (genError) {
      console.error('Generate transaction no error:', genError);
      return NextResponse.json(
        { success: false, error: genError?.message || 'ê±°ë˜ë²ˆí˜¸ ìƒì„± ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    // Create transaction
    const { data, error } = await supabase
      .from('purchase_transactions')
      .insert({
        ...result.data,
        transaction_no: transactionNo,
        payment_status: result.data.payment_status || 'PENDING',
        payment_amount: result.data.payment_amount || 0,
        balance_amount: result.data.balance_amount || result.data.total_amount,
        is_active: true
      })
      .select(`
        *,
        supplier:companies!supplier_id(company_id, company_name, company_code),
        item:items!item_id(item_id, item_name, item_code)
      `)
      .single();

    if (error) {
      console.error('Purchase create error:', error);
      return NextResponse.json(
        { success: false, error: error?.message || 'ë§¤ì… ê±°ë˜ ìƒì„± ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    // Update item stock (increase) - FIXED: Use proper stock update
    const newStock = (item.current_stock || 0) + result.data.quantity;
    const { error: stockError } = await supabase
      .from('items')
      .update({
        current_stock: newStock,
        updated_at: new Date().toISOString()
      })
      .eq('item_id', result.data.item_id);

    if (stockError) {
      console.error('Stock update error:', stockError);
      // Rollback transaction by deleting
      await supabase
        .from('purchase_transactions')
        .delete()
        .eq('transaction_id', data.transaction_id);

      return NextResponse.json(
        { success: false, error: 'ì¬ê³  ì—…ë°ì´íŠ¸ ì‹¤íŒ¨ë¡œ ê±°ë˜ê°€ ì·¨ì†Œë˜ì—ˆìŠµë‹ˆë‹¤' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      success: true,
      data,
      message: 'ë§¤ì… ê±°ë˜ê°€ ìƒì„±ë˜ê³  ì¬ê³ ê°€ ì¦ê°€ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Purchase create error:', error);
