// DELETE: ë§¤ì… ê±°ë˜ ì‚­ì œ (soft delete, ì¬ê³  ê°ì†Œ)
export const DELETE = async (request: NextRequest) => {
  try {
    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id) {
      return NextResponse.json(
        { success: false, error: 'ê±°ë˜ IDê°€ í•„ìš”í•©ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    const supabase = getSupabaseClient();

    // Get transaction data for stock reversal
    const { data: transactionData, error: fetchError } = await supabase
      .from('purchase_transactions')
      .select('item_id, quantity')
      .eq('transaction_id', id)
      .single();

    if (fetchError || !transactionData) {
      return NextResponse.json(
        { success: false, error: 'ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // Soft delete transaction
    const { data, error } = await supabase
      .from('purchase_transactions')
      .update({
        is_active: false,
        updated_at: new Date().toISOString()
      })
      .eq('transaction_id', id)
      .select()
      .single();

    if (error) {
      console.error('Purchase delete error:', error);
      return NextResponse.json(
        { success: false, error: error.message },
        { status: 500 }
      );
    }

    if (!data) {
      return NextResponse.json(
        { success: false, error: 'ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    // Decrease stock (reverse the purchase)
    const { data: currentItem, error: getItemError } = await supabase
      .from('items')
      .select('current_stock')
      .eq('item_id', transactionData.item_id)
      .single();

    if (!getItemError && currentItem) {
      const newStock = (currentItem.current_stock || 0) - transactionData.quantity;

      // Prevent negative stock
      if (newStock < 0) {
        // Rollback soft delete
        await supabase
          .from('purchase_transactions')
          .update({ is_active: true })
          .eq('transaction_id', id);

        return NextResponse.json(
          { success: false, error: 'ì¬ê³ ê°€ ë¶€ì¡±í•˜ì—¬ ê±°ë˜ë¥¼ ì‚­ì œí•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
          { status: 400 }
        );
      }

      const { error: stockError } = await supabase
        .from('items')
        .update({
          current_stock: newStock,
          updated_at: new Date().toISOString()
        })
        .eq('item_id', transactionData.item_id);

      if (stockError) {
        console.error('Stock reversal error:', stockError);
        // Continue even if stock update fails, transaction is already marked deleted
      }
    }

    return NextResponse.json({
      success: true,
      message: 'ë§¤ì… ê±°ë˜ê°€ ì‚­ì œë˜ê³  ì¬ê³ ê°€ ì¡°ì •ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Purchase delete error:', error);
    return NextResponse.json(
      { success: false, error: 'ë§¤ì… ê±°ë˜ ì‚­ì œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
