 * íƒœì°½ ìë™ì°¨ ë¶€í’ˆ ì œì¡° ERP ì‹œìŠ¤í…œ ì¸ì‡„ ìœ í‹¸ë¦¬í‹°
 */

export interface PrintOptions {
  orientation?: 'portrait' | 'landscape';
  pageSize?: 'A4' | 'A3';
  includeHeader?: boolean;
  includeFooter?: boolean;
  showPageNumbers?: boolean;
  title?: string;
  subtitle?: string;
}

export interface PrintPreviewOptions extends PrintOptions {
  onPrint?: () => void;
  onCancel?: () => void;
}

export interface TableColumn {
  key: string;
  label: string;
  align?: 'left' | 'center' | 'right';
  width?: string;
  type?: 'text' | 'number' | 'currency' | 'date';
}

/**
 * í•œêµ­ì–´ ë‚ ì§œ/ì‹œê°„ í¬ë§·íŒ…
 */
export function formatKoreanDateTime(date: Date = new Date()): string {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');

  return `${year}ë…„ ${month}ì›” ${day}ì¼ ${hours}:${minutes}`;
}

/**
 * í•œêµ­ì–´ ë‚ ì§œ í¬ë§·íŒ…
 */
export function formatKoreanDate(date: Date = new Date()): string {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');

  return `${year}ë…„ ${month}ì›” ${day}ì¼`;
}

/**
 * ìˆ«ì í•œêµ­ì–´ í¬ë§·íŒ… (ì²œ ë‹¨ìœ„ ì‰¼í‘œ)
 */
export function formatKoreanNumber(value: number | string): string {
  const num = typeof value === 'string' ? parseFloat(value) : value;
  if (isNaN(num)) return '-';
  return num.toLocaleString('ko-KR');
}

/**
 * í†µí™” í•œêµ­ì–´ í¬ë§·íŒ…
 */
export function formatKoreanCurrency(value: number | string): string {
  const num = typeof value === 'string' ? parseFloat(value) : value;
  if (isNaN(num)) return '-';
  return `â‚©${num.toLocaleString('ko-KR')}`;
}

/**
 * íšŒì‚¬ ì •ë³´ í—¤ë” ìƒì„±
 */
function createCompanyHeader(title: string, subtitle?: string): string {
  const printDate = formatKoreanDateTime();

  return `
    <div class="print-header">
      <h1>íƒœì°½ ìë™ì°¨ ë¶€í’ˆ ì œì¡° ERP ì‹œìŠ¤í…œ</h1>
      <div class="company-info">ê²½ê¸°ë„ ì•ˆì‚°ì‹œ ë‹¨ì›êµ¬ ì‹ ê¸¸ë™ 1234-5 | TEL: 031-123-4567</div>
      <div class="report-title">${title}</div>
      ${subtitle ? `<div class="subtitle">${subtitle}</div>` : ''}
      <div class="print-date">ì¸ì‡„ì¼ì‹œ: ${printDate}</div>
    </div>
  `;
}

/**
 * í…Œì´ë¸” HTML ìƒì„±
 */
function createTableHTML(
  data: Record<string, any>[],
  columns: TableColumn[],
  className: string = 'print-table'
): string {
  if (data.length === 0) {
    return `<div class="no-data">ì¸ì‡„í•  ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.</div>`;
  }

  const tableHeader = columns.map(col =>
    `<th class="text-${col.align || 'left'}" ${col.width ? `style="width: ${col.width}"` : ''}>${col.label}</th>`
  ).join('');

  const tableRows = data.map((row: Record<string, any>) => {
    const cells = columns.map(col => {
      let value = row[col.key] || '-';

      // íƒ€ì…ë³„ í¬ë§·íŒ…
      switch (col.type) {
        case 'number':
          value = formatKoreanNumber(value);
          break;
        case 'currency':
          value = formatKoreanCurrency(value);
          break;
        case 'date':
          if (value && value !== '-') {
            const date = new Date(value);
            value = formatKoreanDate(date);
          }
          break;
      }

      return `<td class="text-${col.align || 'left'} ${col.type || ''}">${value}</td>`;
    }).join('');

    return `<tr>${cells}</tr>`;
  }).join('');

  return `
    <table class="${className}">
      <thead>
        <tr>${tableHeader}</tr>
      </thead>
      <tbody>
        ${tableRows}
      </tbody>
    </table>
  `;
}

/**
 * ì¸ì‡„ìš© ì½˜í…ì¸  ìƒì„±
 */
export function generatePrintableContent(
  content: string,
  options: PrintOptions = {}
): string {
  const {
    orientation = 'portrait',
    includeHeader = true,
    includeFooter = true,
    title = 'ë³´ê³ ì„œ',
    subtitle
  } = options;

  const layoutClass = orientation === 'landscape' ? 'print-landscape' : 'print-portrait';
  const header = includeHeader ? createCompanyHeader(title, subtitle) : '';
  const footer = includeFooter ? `
    <div class="print-footer">
      <div>íƒœì°½ ìë™ì°¨ ë¶€í’ˆ ì œì¡° Â© ${new Date().getFullYear()} | ê¸°ë°€ë¬¸ì„œ</div>
    </div>
  ` : '';

  return `
    <!DOCTYPE html>
    <html lang="ko">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>${title} - íƒœì°½ ERP</title>
      <link rel="stylesheet" href="/styles/print.css">
      <style>
        body { margin: 0; padding: 0; }
        .print-container {
          font-family: 'Malgun Gothic', 'ë§‘ì€ ê³ ë”•', sans-serif;
        }
      </style>
    </head>
    <body class="${layoutClass}">
      <div class="print-container">
        ${header}
        <div class="content">
          ${content}
        </div>
        ${footer}
      </div>
    </body>
    </html>
  `;
}

/**
 * ê¸°ë³¸ í˜ì´ì§€ ì¸ì‡„
 */
export async function printPage(options: PrintOptions = {}): Promise<void> {
  const {
