        {/* Search Results Summary */}
        <div className="flex items-center justify-between mt-3 text-sm text-gray-600 dark:text-gray-400">
          <div>
            {loading || searchLoading ? (
              <span>ê²€ìƒ‰ ì¤‘...</span>
