      normalized.safety_stock === null &&
      normalized.current_stock === null &&
      normalized.location === null &&
      normalized.description === null
    ) {
      throw new APIError('ìˆ˜ì •í•  ê°’ì´ ì—†ìŠµë‹ˆë‹¤.', 400);
    }

    const supabase = getSupabaseClient();
