    }

    if (!data) {
      throw new APIError('ìˆ˜ì • ëŒ€ìƒ í’ˆëª©ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
    }

    return NextResponse.json({
