  { korean: 'í™œì„±ì—¬ë¶€', english: 'is_active', type: 'boolean', default: true }
];

// íšŒì‚¬ ë§¤í•‘
export const companiesMapping: ColumnMapping[] = [
  { korean: 'íšŒì‚¬ì½”ë“œ', english: 'company_code', type: 'string', required: true },
  { korean: 'íšŒì‚¬ëª…', english: 'company_name', type: 'string', required: true },
  { korean: 'íšŒì‚¬êµ¬ë¶„', english: 'company_type', type: 'string', required: true },
  { korean: 'ë‹´ë‹¹ì', english: 'contact_person', type: 'string' },
  { korean: 'ì „í™”ë²ˆí˜¸', english: 'phone', type: 'string' },
  { korean: 'ì´ë©”ì¼', english: 'email', type: 'string' },
  { korean: 'ì£¼ì†Œ', english: 'address', type: 'string' },
  { korean: 'í™œì„±ì—¬ë¶€', english: 'is_active', type: 'boolean', default: true }
];

// BOM ë§¤í•‘
export const bomMapping: ColumnMapping[] = [
  { korean: 'ìƒìœ„í’ˆëª©ì½”ë“œ', english: 'parent_item_code', type: 'string', required: true },
  { korean: 'í•˜ìœ„í’ˆëª©ì½”ë“œ', english: 'child_item_code', type: 'string', required: true },
  { korean: 'ì†Œìš”ëŸ‰', english: 'quantity', type: 'number', required: true },
  { korean: 'ë‹¨ìœ„', english: 'unit', type: 'string', required: true },
  { korean: 'ë¹„ê³ ', english: 'remarks', type: 'string' }
];

// ì¬ê³  íŠ¸ëœì­ì…˜ ë§¤í•‘
export const inventoryMapping: ColumnMapping[] = [
  { korean: 'ê±°ë˜ì¼ì', english: 'transaction_date', type: 'date', required: true },
  { korean: 'ê±°ë˜ìœ í˜•', english: 'transaction_type', type: 'string', required: true },
  { korean: 'í’ˆëª©ì½”ë“œ', english: 'item_code', type: 'string', required: true },
  { korean: 'ìˆ˜ëŸ‰', english: 'quantity', type: 'number', required: true },
  { korean: 'ë‹¨ìœ„', english: 'unit', type: 'string', required: true },
  { korean: 'íšŒì‚¬ì½”ë“œ', english: 'company_code', type: 'string' },
  { korean: 'ì°¸ì¡°ë²ˆí˜¸', english: 'reference_number', type: 'string' },
  { korean: 'ë¹„ê³ ', english: 'remarks', type: 'string' }
];

// ë§¤í•‘ ë§µ
export const mappings = {
  items: itemsMapping,
  companies: companiesMapping,
  bom: bomMapping,
  inventory: inventoryMapping
};

// ë°ì´í„° ë³€í™˜ í•¨ìˆ˜
export function convertExcelData(data: Record<string, any>[], mapping: ColumnMapping[]): Record<string, any>[] {
  return data.map(row => {
    const converted: Record<string, any> = {};

    mapping.forEach(col => {
      const koreanValue = row[col.korean];
      let value = koreanValue;

      // ë¹ˆ ê°’ ì²˜ë¦¬
      if (value === undefined || value === null || value === '') {
        if (col.required) {
          throw new Error(`í•„ìˆ˜ í•­ëª©ì´ ë¹„ì–´ìˆìŠµë‹ˆë‹¤: ${col.korean}`);
        }
        value = col.default;
      }

      // íƒ€ì… ë³€í™˜
      switch (col.type) {
        case 'number':
          if (typeof value === 'string') {
            value = parseFloat(value.replace(/,/g, ''));
            if (isNaN(value)) {
              throw new Error(`ìˆ«ì í˜•ì‹ì´ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤: ${col.korean} = ${koreanValue}`);
            }
          }
          break;

        case 'date':
          if (typeof value === 'string') {
            // Excel ë‚ ì§œ í˜•ì‹ ì²˜ë¦¬ (YYYY-MM-DD, YYYY.MM.DD, YYYY/MM/DD)
            const dateStr = value.replace(/[.\/]/g, '-');
            const date = new Date(dateStr);
            if (isNaN(date.getTime())) {
              throw new Error(`ë‚ ì§œ í˜•ì‹ì´ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤: ${col.korean} = ${koreanValue}`);
            }
            value = date.toISOString().split('T')[0];
          }
          break;

        case 'boolean':
          if (typeof value === 'string') {
            const lowerValue = value.toLowerCase();
            if (['true', 'ì°¸', 'y', 'yes', '1', 'o', 'x'].includes(lowerValue)) {
              value = lowerValue === 'true' || lowerValue === 'ì°¸' || lowerValue === 'y' ||
                     lowerValue === 'yes' || lowerValue === '1' || lowerValue === 'o';
            } else {
              value = Boolean(value);
            }
          }
          break;

        case 'string':
          if (value !== null && value !== undefined) {
            value = String(value).trim();
          }
          break;
      }

      converted[col.english] = value;
    });

    return converted;
  });
}

// í•œê¸€ í—¤ë”ë¥¼ ì˜ë¬¸ í•„ë“œë¡œ ë§¤í•‘
export function mapKoreanToEnglish(data: Record<string, any>[], mapping: ColumnMapping[]): any[] {
  return data.map(row => {
    const mapped: any = {};
    mapping.forEach(col => {
      if (row.hasOwnProperty(col.korean)) {
        mapped[col.english] = row[col.korean];
      }
    });
    return mapped;
  });
}

// ì˜ë¬¸ í•„ë“œë¥¼ í•œê¸€ í—¤ë”ë¡œ ë§¤í•‘ (Exportìš©)
export function mapEnglishToKorean(data: Record<string, any>[], mapping: ColumnMapping[]): any[] {
  return data.map(row => {
    const mapped: any = {};
    mapping.forEach(col => {
      if (row.hasOwnProperty(col.english)) {
        mapped[col.korean] = row[col.english];
      }
    });
    return mapped;
  });
}

// íšŒì‚¬ êµ¬ë¶„ ë§¤í•‘
export function mapCompanyType(type: string): string {
  const mapping: { [key: string]: string } = {
    'CUSTOMER': 'ê³ ê°ì‚¬',
    'SUPPLIER': 'ê³µê¸‰ì‚¬',
    'ê³ ê°ì‚¬': 'CUSTOMER',
    'ê³µê¸‰ì‚¬': 'SUPPLIER'
  };
  return mapping[type] || type;
}

// ê±°ë˜ ìœ í˜• ë§¤í•‘
export function mapTransactionType(type: string): string {
  const mapping: { [key: string]: string } = {
    'RECEIVING': 'ì…ê³ ',
    'PRODUCTION': 'ìƒì‚°',
    'SHIPPING': 'ì¶œê³ ',
    'ì…ê³ ': 'RECEIVING',
    'ìƒì‚°': 'PRODUCTION',
    'ì¶œê³ ': 'SHIPPING'
  };
  return mapping[type] || type;
}

// Excel í…œí”Œë¦¿ ìƒì„±ìš© ë¹ˆ ë°ì´í„°
export function createTemplate(mapping: ColumnMapping[]): Record<string, string> {
  const template: Record<string, string> = {};
  mapping.forEach(col => {
    template[col.korean] = '';
  });
  return template;
}

// ìœ íš¨ì„± ê²€ì‚¬
export function validateData(data: Record<string, any>[], mapping: ColumnMapping[]): { isValid: boolean; errors: string[] } {
  const errors: string[] = [];

  data.forEach((row, index) => {
    mapping.forEach(col => {
      const value = row[col.english];

      // í•„ìˆ˜ í•„ë“œ ê²€ì‚¬
      if (col.required && (value === undefined || value === null || value === '')) {
        errors.push(`í–‰ ${index + 1}: ${col.korean}ì€(ëŠ”) í•„ìˆ˜ í•­ëª©ì…ë‹ˆë‹¤.`);
      }

      // íƒ€ì… ê²€ì‚¬
      if (value !== null && value !== undefined && value !== '') {
        switch (col.type) {
          case 'number':
            if (typeof value !== 'number' || isNaN(value)) {
              errors.push(`í–‰ ${index + 1}: ${col.korean}ì€(ëŠ”) ìˆ«ìì—¬ì•¼ í•©ë‹ˆë‹¤.`);
            }
            break;
          case 'date':
            if (typeof value === 'string') {
              const date = new Date(value);
              if (isNaN(date.getTime())) {
                errors.push(`í–‰ ${index + 1}: ${col.korean}ì€(ëŠ”) ì˜¬ë°”ë¥¸ ë‚ ì§œ í˜•ì‹ì´ì–´ì•¼ í•©ë‹ˆë‹¤.`);
              }
            }
            break;
          case 'boolean':
            if (typeof value !== 'boolean') {
              errors.push(`í–‰ ${index + 1}: ${col.korean}ì€(ëŠ”) true/false ê°’ì´ì–´ì•¼ í•©ë‹ˆë‹¤.`);
            }
            break;
        }
      }
    });
  });

  return {
    isValid: errors.length === 0,
    errors
  };
}

