import { NextRequest, NextResponse } from 'next/server';
import * as XLSX from 'xlsx';
import { createClient } from '@supabase/supabase-js';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const search = searchParams.get('search');
    const payment_method = searchParams.get('payment_method');
    const start_date = searchParams.get('start_date');
    const end_date = searchParams.get('end_date');

    // Initialize Supabase client
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Build query with joins
    let query = supabase
      .from('collection_transactions')
      .select(`
        collection_id,
        collection_no,
        collection_date,
        collected_amount,
        payment_method,
        bank_name,
        account_number,
        check_number,
        card_number,
        notes,
        is_active,
        created_at,
        updated_at,
        sales_transaction:sales_transactions!sales_transaction_id(
          transaction_no,
          transaction_date,
          total_amount,
          payment_status
        ),
        customer:companies!customer_id(
          company_name,
          company_code,
          business_number
        )
      `)
      .eq('is_active', true);

    // Apply filters
    if (start_date) {
      query = query.gte('collection_date', start_date);
    }

    if (end_date) {
      query = query.lte('collection_date', end_date);
    }

    if (payment_method) {
      query = query.eq('payment_method', payment_method);
    }

    if (search) {
      query = query.or(`collection_no.ilike.%${search}%`);
    }

    // Apply ordering
    query = query.order('collection_date', { ascending: false });

    const { data: collections, error } = await query;

    if (error) {
      throw new Error(`Database query failed: ${error.message}`);
    }

    // Transform data with Korean headers
    const formattedCollections = collections?.map((collection: any) => ({
      "ìˆ˜ê¸ˆID": collection.collection_id,
      "ìˆ˜ê¸ˆë²ˆí˜¸": collection.collection_no,
      "ìˆ˜ê¸ˆì¼ì": collection.collection_date,
      "ë§¤ì¶œë²ˆí˜¸": collection.sales_transaction?.transaction_no || '',
      "ë§¤ì¶œì¼ì": collection.sales_transaction?.transaction_date || '',
      "ê³ ê°ì‚¬ëª…": collection.customer?.company_name || '',
      "ì‚¬ì—…ìë²ˆí˜¸": collection.customer?.business_number || '',
      "ìˆ˜ê¸ˆê¸ˆì•¡": collection.collected_amount,
      "ê²°ì œë°©ë²•": collection.payment_method === 'CASH' ? 'í˜„ê¸ˆ' :
                   collection.payment_method === 'TRANSFER' ? 'ê³„ì¢Œì´ì²´' :
                   collection.payment_method === 'CHECK' ? 'ìˆ˜í‘œ' :
                   collection.payment_method === 'CARD' ? 'ì¹´ë“œ' : collection.payment_method,
      "ì€í–‰ëª…": collection.bank_name || '',
      "ê³„ì¢Œë²ˆí˜¸": collection.account_number || '',
      "ìˆ˜í‘œë²ˆí˜¸": collection.check_number || '',
      "ì¹´ë“œë²ˆí˜¸": collection.card_number || '',
      "ë¹„ê³ ": collection.notes || '',
      "ë§¤ì¶œê¸ˆì•¡": collection.sales_transaction?.total_amount || 0,
      "ìˆ˜ê¸ˆìƒíƒœ": collection.sales_transaction?.payment_status === 'PENDING' ? 'ë¯¸ìˆ˜ê¸ˆ' :
                 collection.sales_transaction?.payment_status === 'PARTIAL' ? 'ë¶€ë¶„ìˆ˜ê¸ˆ' :
                 collection.sales_transaction?.payment_status === 'COMPLETED' ? 'ì™„ë£Œ' : '',
      "ë“±ë¡ì¼ì‹œ": new Date(collection.created_at).toLocaleString('ko-KR'),
