import { NextRequest, NextResponse } from 'next/server';
import { getSupabaseClient } from '@/lib/db-unified';
import { z } from 'zod';

// íŒë§¤ ê±°ë˜ ìƒì„± ìŠ¤í‚¤ë§ˆ
const SalesTransactionCreateSchema = z.object({
  transaction_date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/, 'ë‚ ì§œ í˜•ì‹: YYYY-MM-DD'),
  customer_id: z.number().positive('ê³ ê°ì‚¬ IDëŠ” ì–‘ìˆ˜ì—¬ì•¼ í•©ë‹ˆë‹¤'),
  item_id: z.number().positive('í’ˆëª© IDëŠ” ì–‘ìˆ˜ì—¬ì•¼ í•©ë‹ˆë‹¤'),
  item_name: z.string().min(1, 'í’ˆëª©ëª…ì€ í•„ìˆ˜ì…ë‹ˆë‹¤'),
  spec: z.string().optional(),
  quantity: z.number().positive('ìˆ˜ëŸ‰ì€ 0ë³´ë‹¤ ì»¤ì•¼ í•©ë‹ˆë‹¤'),
  unit_price: z.number().min(0, 'ë‹¨ê°€ëŠ” 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  supply_amount: z.number().min(0, 'ê³µê¸‰ê°€ëŠ” 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  tax_amount: z.number().min(0, 'ì„¸ì•¡ì€ 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤').optional(),
  total_amount: z.number().min(0, 'ì´ì•¡ì€ 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤'),
  payment_status: z.enum(['PENDING', 'PARTIAL', 'COMPLETED']).optional(),
  payment_due_date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/).optional(),
  delivery_address: z.string().optional(),
  delivery_date: z.string().regex(/^\d{4}-\d{2}-\d{2}$/).optional(),
  notes: z.string().optional()
});

// GET: íŒë§¤ ê±°ë˜ ëª©ë¡ ì¡°íšŒ
export const GET = async (request: NextRequest) => {
  try {
    const searchParams = request.nextUrl.searchParams;
    const page = Math.max(1, parseInt(searchParams.get('page') || '1', 10) || 1);
    const limit = Math.min(100, Math.max(1, parseInt(searchParams.get('limit') || '20', 10) || 20));
    const offset = (page - 1) * limit;

    const customer_id = searchParams.get('customer_id');
    const item_id = searchParams.get('item_id');
    const payment_status = searchParams.get('payment_status');
    const start_date = searchParams.get('start_date');
    const end_date = searchParams.get('end_date');
    const search = searchParams.get('search');
    const orderBy = searchParams.get('orderBy') || 'transaction_date';
    const order = searchParams.get('order') || 'desc';

    const supabase = getSupabaseClient();

    // Build query
    let query = supabase
      .from('sales_transactions')
      .select(`
        *,
        customer:companies!customer_id(company_id, company_name, company_code),
        item:items!item_id(item_id, item_name, item_code)
      `, { count: 'exact' })
      .eq('is_active', true);

    // Apply filters
    if (customer_id) {
      query = query.eq('customer_id', customer_id);
    }

    if (item_id) {
      query = query.eq('item_id', item_id);
    }

    if (payment_status) {
      query = query.eq('payment_status', payment_status);
    }

    if (start_date) {
      query = query.gte('transaction_date', start_date);
    }

    if (end_date) {
      query = query.lte('transaction_date', end_date);
    }

    if (search) {
      query = query.or(`transaction_no.ilike.%${search}%,item_name.ilike.%${search}%`);
    }

    // Apply ordering and pagination
    query = query
      .order(orderBy, { ascending: order === 'asc' })
      .range(offset, offset + limit - 1);

    const { data, error, count } = await query;

    if (error) {
      console.error('Sales list error:', error);
      return NextResponse.json(
        { success: false, error: error?.message || 'íŒë§¤ ê±°ë˜ ì¡°íšŒ ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      success: true,
      data: data || [],
      pagination: {
        page,
        limit,
        total: count || 0,
        totalPages: Math.ceil((count || 0) / limit)
      }
    });
  } catch (error) {
    console.error('Sales list error:', error);
    return NextResponse.json(
      { success: false, error: 'íŒë§¤ ê±°ë˜ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
    );
  }
};

// POST: íŒë§¤ ê±°ë˜ ìƒì„±
export const POST = async (request: NextRequest) => {
  try {
    const text = await request.text();
    const body = JSON.parse(text);

    // Validate input
    const result = SalesTransactionCreateSchema.safeParse(body);
    if (!result.success) {
      const errorMessages = result.error.errors.map(err => err.message).join(', ');
      return NextResponse.json(
        { success: false, error: errorMessages || 'ì…ë ¥ ë°ì´í„°ê°€ ìœ íš¨í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤' },
        { status: 400 }
