      .rpc('generate_sales_no');

    if (genError) {
      console.error('Generate transaction no error:', genError);
      return NextResponse.json(
        { success: false, error: genError?.message || 'ê±°ë˜ë²ˆí˜¸ ìƒì„± ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    // Create transaction
    const { data, error } = await supabase
      .from('sales_transactions')
      .insert({
        ...result.data,
        transaction_no: transactionNo,
        payment_status: result.data.payment_status || 'PENDING',
        is_active: true
      })
      .select(`
        *,
        customer:companies!customer_id(company_id, company_name, company_code),
        item:items!item_id(item_id, item_name, item_code)
      `)
      .single();

    if (error) {
      console.error('Sales create error:', error);
      return NextResponse.json(
        { success: false, error: error?.message || 'íŒë§¤ ê±°ë˜ ìƒì„± ì‹¤íŒ¨' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      success: true,
      data,
      message: 'íŒë§¤ ê±°ë˜ê°€ ìƒì„±ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Sales create error:', error);
    const errorMessage = error instanceof Error ? error.message : 'íŒë§¤ ê±°ë˜ ìƒì„± ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤';
    return NextResponse.json(
