};

// PUT: íŒë§¤ ê±°ë˜ ìˆ˜ì •
export const PUT = async (request: NextRequest) => {
  try {
    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id) {
      return NextResponse.json(
        { success: false, error: 'ê±°ë˜ IDê°€ í•„ìš”í•©ë‹ˆë‹¤' },
        { status: 400 }
      );
    }

    const text = await request.text();
    const body = JSON.parse(text);

    // Validate update data (use partial schema for optional updates)
    const UpdateSchema = SalesTransactionCreateSchema.partial();
    const result = UpdateSchema.safeParse(body);
    if (!result.success) {
      const errorMessages = result.error.errors.map(err => err.message).join(', ');
      return NextResponse.json(
        { success: false, error: errorMessages || 'ì…ë ¥ ë°ì´í„°ê°€ ìœ íš¨í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤' },
        { status: 400 }
