    }

    if (!data) {
      return NextResponse.json(
        { success: false, error: 'ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
        { status: 404 }
      );
    }

    return NextResponse.json({
      success: true,
      data,
      message: 'íŒë§¤ ê±°ë˜ê°€ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤'
    });
  } catch (error) {
    console.error('Sales update error:', error);
    return NextResponse.json(
      { success: false, error: 'íŒë§¤ ê±°ë˜ ìˆ˜ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤' },
      { status: 500 }
