    ];
    worksheet['!cols'] = columnWidths;

    // Add metadata sheet
    const metadataRows = [
      ['ë‚´ë³´ë‚´ê¸° ì •ë³´', ''],
      ['ë‚´ë³´ë‚¸ ë‚ ì§œ', new Date().toLocaleString('ko-KR')],
      ['ì´ íšŒì‚¬ ìˆ˜', companies.length],
      ['í•„í„°', ''],
      ['íšŒì‚¬êµ¬ë¶„', type || 'ì „ì²´'],
      ['ê²€ìƒ‰ì–´', search || 'ì—†ìŒ'],
      ['', ''],
      ['íƒœì°½ ERP ì‹œìŠ¤í…œ', 'íšŒì‚¬ ëª©ë¡ ë‚´ë³´ë‚´ê¸°']
    ];

    const metadataSheet = XLSX.utils.aoa_to_sheet(metadataRows);
