      ['ë¹„í™œì„± íšŒì‚¬', companies.filter((c: any) => c['ìƒíƒœ'] === 'ë¹„í™œì„±').length]
    ];

    const statsSheet = XLSX.utils.aoa_to_sheet(statsRows);
    statsSheet['!cols'] = [{ wch: 15 }, { wch: 15 }];

    // Add sheets to workbook
