import { NextRequest, NextResponse } from 'next/server';
import { supabaseAdmin } from '@/lib/supabase';
import * as XLSX from 'xlsx';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const type = searchParams.get('type');
    const status = searchParams.get('status');
    const search = searchParams.get('search');

    // Build Supabase query
    let query = supabaseAdmin
      .from('items')
      .select('*')
      .eq('is_active', true)
      .order('item_code');

    if (type) {
      query = query.eq('category', type as "ì›ìì¬" | "ë¶€ìì¬" | "ë°˜ì œí’ˆ" | "ì œí’ˆ" | "ìƒí’ˆ");
    }

    if (search) {
      query = query.or(`item_code.ilike.%${search}%,item_name.ilike.%${search}%,spec.ilike.%${search}%`);
    }

    const { data: items, error } = await query;

    if (error) {
      throw new Error(error.message);
    }

    // Transform data for export
    const stockData = (items || []).map(item => {
      const currentStock = item.current_stock || 0;
      const minStock = item.safety_stock || 0;
      const unitPrice = item.price || 0;
      const stockValue = currentStock * unitPrice;

      let statusText = 'ì •ìƒ';
      let statusCode = 'NORMAL';

      if (currentStock <= 0) {
        statusText = 'ì¬ê³ ì—†ìŒ';
        statusCode = 'OUT_OF_STOCK';
      } else if (minStock > 0 && currentStock <= minStock * 0.5) {
        statusText = 'ë¶€ì¡±';
        statusCode = 'LOW_STOCK';
      } else if (minStock > 0 && currentStock <= minStock) {
        statusText = 'ì£¼ì˜';
        statusCode = 'WARNING';
      }

      const categoryMap: Record<string, string> = {
        'ì›ìì¬': 'ì›ìì¬',
        'ë¶€ìì¬': 'ë¶€ìì¬',
        'ë°˜ì œí’ˆ': 'ë°˜ì œí’ˆ',
        'ì œí’ˆ': 'ì œí’ˆ',
        'ìƒí’ˆ': 'ìƒí’ˆ'
      };

      return {
        'í’ˆëª©ID': item.item_id,
        'í’ˆëª©ì½”ë“œ': item.item_code,
        'í’ˆëª©ëª…': item.item_name,
        'í’ˆëª©êµ¬ë¶„': categoryMap[item.category] || item.category,
        'ê·œê²©': item.spec,
        'ë‹¨ìœ„': item.unit,
        'í˜„ì¬ì¬ê³ ': currentStock,
        'ì•ˆì „ì¬ê³ ': minStock,
        'ë‹¨ê°€': unitPrice,
        'ì¬ê³ ê¸ˆì•¡': stockValue,
        'ìœ„ì¹˜': item.location,
        'ì¬ê³ ìƒíƒœ': statusText,
        'status_code': statusCode,
        'ì„¤ëª…': item.description,
        'ë“±ë¡ì¼': item.created_at?.split('T')[0],
        'ìˆ˜ì •ì¼': item.updated_at?.split('T')[0]
      };
    });

    // Filter by status if specified
    let filteredData = stockData;
    if (status) {
      filteredData = stockData.filter(item => {
        const statusCode = item['status_code'];
        switch (status) {
          case 'low': return statusCode === 'LOW_STOCK';
          case 'warning': return statusCode === 'WARNING';
          case 'normal': return statusCode === 'NORMAL';
          case 'out': return statusCode === 'OUT_OF_STOCK';
          default: return true;
        }
      });
    }

    // Remove status_code column before export
    const exportData = filteredData.map(({ status_code: _, ...rest }) => rest);

    // Create workbook and worksheet
    const workbook = XLSX.utils.book_new();
    const worksheet = XLSX.utils.json_to_sheet(exportData);

    // Set column widths for better formatting
    const columnWidths = [
      { wch: 10 }, // í’ˆëª©ID
      { wch: 15 }, // í’ˆëª©ì½”ë“œ
      { wch: 25 }, // í’ˆëª©ëª…
      { wch: 10 }, // í’ˆëª©êµ¬ë¶„
      { wch: 20 }, // ê·œê²©
      { wch: 8 },  // ë‹¨ìœ„
      { wch: 12 }, // í˜„ì¬ì¬ê³ 
      { wch: 12 }, // ì•ˆì „ì¬ê³ 
      { wch: 12 }, // ë‹¨ê°€
      { wch: 15 }, // ì¬ê³ ê¸ˆì•¡
      { wch: 15 }, // ìœ„ì¹˜
      { wch: 10 }, // ì¬ê³ ìƒíƒœ
      { wch: 25 }, // ì„¤ëª…
      { wch: 12 }, // ë“±ë¡ì¼
      { wch: 12 }  // ìˆ˜ì •ì¼
    ];
    worksheet['!cols'] = columnWidths;

    // Calculate statistics
    const totalItems = exportData.length;
    const totalStockValue = exportData.reduce((sum, item) => sum + (item['ì¬ê³ ê¸ˆì•¡'] || 0), 0);
    const totalStock = exportData.reduce((sum, item) => sum + (item['í˜„ì¬ì¬ê³ '] || 0), 0);

    const statusCounts = stockData.reduce((acc, item) => {
      const status = item['ì¬ê³ ìƒíƒœ'];
      acc[status] = (acc[status] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const typeCounts = exportData.reduce((acc, item) => {
      const type = item['í’ˆëª©êµ¬ë¶„'];
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    // Add metadata sheet
    const metadataRows = [
      ['ë‚´ë³´ë‚´ê¸° ì •ë³´', ''],
      ['ë‚´ë³´ë‚¸ ë‚ ì§œ', new Date().toLocaleString('ko-KR')],
      ['ì´ í’ˆëª© ìˆ˜', totalItems],
      ['ì´ ì¬ê³ ëŸ‰', totalStock.toLocaleString('ko-KR')],
      ['ì´ ì¬ê³ ê¸ˆì•¡', totalStockValue.toLocaleString('ko-KR') + 'ì›'],
      ['í•„í„°', ''],
      ['í’ˆëª©êµ¬ë¶„', type || 'ì „ì²´'],
      ['ì¬ê³ ìƒíƒœ', status || 'ì „ì²´'],
      ['ê²€ìƒ‰ì–´', search || 'ì—†ìŒ'],
      ['', ''],
      ['íƒœì°½ ERP ì‹œìŠ¤í…œ', 'ì¬ê³  í˜„í™© ë‚´ë³´ë‚´ê¸°']
    ];

    const metadataSheet = XLSX.utils.aoa_to_sheet(metadataRows);
    metadataSheet['!cols'] = [{ wch: 15 }, { wch: 25 }];

    // Add statistics sheet
    const statsRows = [
      ['ì¬ê³  ìƒíƒœë³„ í†µê³„', 'í’ˆëª© ìˆ˜'],
      ...Object.entries(statusCounts).map(([status, count]) => [status, count]),
      ['', ''],
      ['í’ˆëª© êµ¬ë¶„ë³„ í†µê³„', 'í’ˆëª© ìˆ˜'],
      ...Object.entries(typeCounts).map(([type, count]) => [type, count]),
      ['', ''],
      ['ì¬ê³  ê¸ˆì•¡ ìƒìœ„ 10ê°œ', ''],
      ...exportData
        .sort((a, b) => (b['ì¬ê³ ê¸ˆì•¡'] || 0) - (a['ì¬ê³ ê¸ˆì•¡'] || 0))
        .slice(0, 10)
        .map(item => [
          `${item['í’ˆëª©ì½”ë“œ']} - ${item['í’ˆëª©ëª…']}`,
          (item['ì¬ê³ ê¸ˆì•¡'] || 0).toLocaleString('ko-KR') + 'ì›'
        ])
    ];

    const statsSheet = XLSX.utils.aoa_to_sheet(statsRows);
    statsSheet['!cols'] = [{ wch: 30 }, { wch: 15 }];

    // Add low stock alert sheet
    const lowStockItems = stockData.filter(item =>
      item['status_code'] === 'LOW_STOCK' ||
      item['status_code'] === 'OUT_OF_STOCK' ||
      item['status_code'] === 'WARNING'
    ).map(({ status_code: _, ...rest }) => rest);

    if (lowStockItems.length > 0) {
      const lowStockSheet = XLSX.utils.json_to_sheet(lowStockItems);
      lowStockSheet['!cols'] = columnWidths;
      XLSX.utils.book_append_sheet(workbook, lowStockSheet, 'ì£¼ì˜ í’ˆëª©');
    }

    // Add sheets to workbook
    XLSX.utils.book_append_sheet(workbook, metadataSheet, 'ë‚´ë³´ë‚´ê¸° ì •ë³´');
    XLSX.utils.book_append_sheet(workbook, statsSheet, 'í†µê³„');
    XLSX.utils.book_append_sheet(workbook, worksheet, 'ì¬ê³  í˜„í™©');

    // Generate Excel file
    const excelBuffer = XLSX.write(workbook, {
      type: 'buffer',
      bookType: 'xlsx'
    });

    // Create filename with current date and filters
    const currentDate = new Date().toISOString().split('T')[0];
    let filename = `ì¬ê³ í˜„í™©_${currentDate}`;
    if (type) filename += `_${type}`;
    if (status) filename += `_${status}`;
    filename += '.xlsx';

    // Return Excel file
    return new NextResponse(excelBuffer, {
      status: 200,
      headers: {
        'Content-Type': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'Content-Disposition': `attachment; filename="${encodeURIComponent(filename)}"`,
        'Content-Length': excelBuffer.length.toString()
      }
    });

  } catch (error) {
    console.error('Error exporting stock status:', error);
    return NextResponse.json(
      {
        success: false,
        error: 'Failed to export stock status to Excel'
      },
      { status: 500 }
    );
  }
}

