import * as XLSX from 'xlsx';

// Generic interfaces for export data
interface ExportColumn {
  key: string;
  header: string;
  width?: number;
  format?: 'text' | 'number' | 'currency' | 'date';
}

interface ExportOptions {
  filename?: string;
  sheetName?: string;
  columns?: ExportColumn[];
  format?: 'xlsx' | 'csv';
  includeTimestamp?: boolean;
}

// Format date for Korean locale
const formatKoreanDate = (date: string | Date): string => {
  const d = new Date(date);
  return d.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit'
  });
};

// Format currency for Korean locale
const formatKoreanCurrency = (amount: number): string => {
  return new Intl.NumberFormat('ko-KR', {
    style: 'currency',
    currency: 'KRW'
  }).format(amount);
};

// Generic export function
export const exportToExcel = (
  data: Record<string, any>[],
  options: ExportOptions = {}
): void => {
  const {
    filename = 'ë°ì´í„°_ë‚´ë³´ë‚´ê¸°',
    sheetName = 'Sheet1',
    columns = [],
    format = 'xlsx',
    includeTimestamp = true
  } = options;

  if (!data || data.length === 0) {
    throw new Error('ë‚´ë³´ë‚¼ ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.');
  }

  // Process data based on columns configuration
  let processedData = data;

  if (columns.length > 0) {
    processedData = data.map((row: Record<string, any>) => {
      const processedRow: Record<string, any> = {};
      columns.forEach(col => {
        let value = row[col.key];

        // Apply formatting based on column type
        switch (col.format) {
          case 'date':
            value = value ? formatKoreanDate(value) : '';
            break;
          case 'currency':
            value = typeof value === 'number' ? formatKoreanCurrency(value) : value;
            break;
          case 'number':
            value = typeof value === 'number' ? value.toLocaleString('ko-KR') : value;
            break;
          default:
            // Keep original value for text
            break;
        }

        processedRow[col.header] = value;
      });
      return processedRow;
    });
  }

  // Create workbook and worksheet
  const wb = XLSX.utils.book_new();
  const ws = XLSX.utils.json_to_sheet(processedData);

  // Set column widths
  if (columns.length > 0) {
    const colWidths = columns.map(col => ({
      wch: col.width || 15
    }));
    ws['!cols'] = colWidths;
  }

  // Add worksheet to workbook
  XLSX.utils.book_append_sheet(wb, ws, sheetName);

  // Generate filename with timestamp
  const timestamp = includeTimestamp
    ? `_${new Date().toISOString().slice(0, 19).replace(/[:-]/g, '').replace('T', '_')}`
    : '';

  const finalFilename = `${filename}${timestamp}.${format}`;

  // Write and download file
  XLSX.writeFile(wb, finalFilename);
};

// Specialized export functions

// Export items with Korean headers
export const exportItems = (items: any[]): void => {
  const columns: ExportColumn[] = [
    { key: 'item_id', header: 'í’ˆë²ˆ', width: 15 },
    { key: 'item_name', header: 'í’ˆëª…', width: 25 },
    { key: 'item_type', header: 'íƒ€ì…', width: 12 },
    { key: 'car_model', header: 'ì°¨ì¢…', width: 15 },
    { key: 'spec', header: 'ê·œê²©', width: 20 },
    { key: 'unit', header: 'ë‹¨ìœ„', width: 10 },
    { key: 'current_stock', header: 'í˜„ì¬ê³ ', width: 12, format: 'number' },
    { key: 'min_stock', header: 'ìµœì†Œì¬ê³ ', width: 12, format: 'number' },
    { key: 'unit_price', header: 'ë‹¨ê°€', width: 15, format: 'currency' },
    { key: 'location', header: 'ìœ„ì¹˜', width: 15 },
    { key: 'created_at', header: 'ë“±ë¡ì¼ì‹œ', width: 18, format: 'date' }
  ];

  exportToExcel(items, {
    filename: 'í’ˆëª©_ëª©ë¡',
    sheetName: 'í’ˆëª©',
    columns,
    format: 'xlsx'
  });
};

// Export companies with Korean headers
export const exportCompanies = (companies: any[]): void => {
  const columns: ExportColumn[] = [
    { key: 'company_id', header: 'ê±°ë˜ì²˜ì½”ë“œ', width: 15 },
    { key: 'company_name', header: 'ê±°ë˜ì²˜ëª…', width: 25 },
    { key: 'company_type', header: 'íƒ€ì…', width: 12 },
    { key: 'business_number', header: 'ì‚¬ì—…ìë“±ë¡ë²ˆí˜¸', width: 18 },
    { key: 'contact_person', header: 'ë‹´ë‹¹ì', width: 15 },
    { key: 'phone', header: 'ì „í™”ë²ˆí˜¸', width: 15 },
    { key: 'email', header: 'ì´ë©”ì¼', width: 20 },
    { key: 'address', header: 'ì£¼ì†Œ', width: 30 },
    { key: 'created_at', header: 'ë“±ë¡ì¼ì‹œ', width: 18, format: 'date' }
  ];

  exportToExcel(companies, {
    filename: 'ê±°ë˜ì²˜_ëª©ë¡',
    sheetName: 'ê±°ë˜ì²˜',
    columns,
    format: 'xlsx'
  });
};

// Export BOM with Korean headers
export const exportBOM = (bomData: any[]): void => {
  const columns: ExportColumn[] = [
    { key: 'parent_item_name', header: 'ëª¨í’ˆëª©', width: 25 },
    { key: 'child_item_name', header: 'ìí’ˆëª©', width: 25 },
    { key: 'quantity', header: 'ì†Œìš”ëŸ‰', width: 12, format: 'number' },
    { key: 'unit', header: 'ë‹¨ìœ„', width: 10 },
    { key: 'notes', header: 'ë¹„ê³ ', width: 20 },
    { key: 'created_at', header: 'ë“±ë¡ì¼ì‹œ', width: 18, format: 'date' }
  ];

  exportToExcel(bomData, {
    filename: 'BOM_ëª©ë¡',
    sheetName: 'BOM',
    columns,
    format: 'xlsx'
  });
};

// Export inventory transactions with Korean headers
export const exportTransactions = (transactions: Record<string, any>[]): void => {
  const columns: ExportColumn[] = [
    { key: 'transaction_id', header: 'ê±°ë˜ë²ˆí˜¸', width: 15 },
    { key: 'item_name', header: 'í’ˆëª©ëª…', width: 25 },
    { key: 'transaction_type', header: 'ê±°ë˜ìœ í˜•', width: 12 },
    { key: 'quantity', header: 'ìˆ˜ëŸ‰', width: 12, format: 'number' },
    { key: 'unit_price', header: 'ë‹¨ê°€', width: 15, format: 'currency' },
    { key: 'total_amount', header: 'ì´ê¸ˆì•¡', width: 15, format: 'currency' },
    { key: 'company_name', header: 'ê±°ë˜ì²˜', width: 20 },
    { key: 'reference_number', header: 'ì°¸ì¡°ë²ˆí˜¸', width: 15 },
    { key: 'notes', header: 'ë¹„ê³ ', width: 20 },
    { key: 'transaction_date', header: 'ê±°ë˜ì¼ì‹œ', width: 18, format: 'date' },
    { key: 'created_at', header: 'ë“±ë¡ì¼ì‹œ', width: 18, format: 'date' }
  ];

  exportToExcel(transactions, {
    filename: 'ì¬ê³ _ê±°ë˜ë‚´ì—­',
    sheetName: 'ê±°ë˜ë‚´ì—­',
    columns,
    format: 'xlsx'
  });
};

// Export stock status with Korean headers
export const exportStockStatus = (stockData: any[]): void => {
  const columns: ExportColumn[] = [
    { key: 'item_name', header: 'í’ˆëª©ëª…', width: 25 },
    { key: 'current_stock', header: 'í˜„ì¬ê³ ', width: 12, format: 'number' },
    { key: 'min_stock', header: 'ìµœì†Œì¬ê³ ', width: 12, format: 'number' },
    { key: 'stock_status', header: 'ì¬ê³ ìƒíƒœ', width: 12 },
    { key: 'unit_price', header: 'ë‹¨ê°€', width: 15, format: 'currency' },
    { key: 'stock_value', header: 'ì¬ê³ ê°€ì¹˜', width: 15, format: 'currency' },
    { key: 'location', header: 'ìœ„ì¹˜', width: 15 },
    { key: 'last_updated', header: 'ìµœì¢…ì—…ë°ì´íŠ¸', width: 18, format: 'date' }
  ];

  exportToExcel(stockData, {
    filename: 'ì¬ê³ _í˜„í™©',
    sheetName: 'ì¬ê³ í˜„í™©',
    columns,
    format: 'xlsx'
  });
};

// Multi-sheet export function
export const exportMultipleSheets = (
  sheetsData: { data: Record<string, any>[], options: ExportOptions }[]
): void => {
  const wb = XLSX.utils.book_new();

  sheetsData.forEach(({ data, options }) => {
    if (!data || data.length === 0) return;

    const {
      sheetName = 'Sheet',
      columns = []
    } = options;

    // Process data based on columns configuration
    let processedData = data;

    if (columns.length > 0) {
      processedData = data.map((row: Record<string, any>) => {
        const processedRow: Record<string, any> = {};
        columns.forEach(col => {
          let value = row[col.key];

          // Apply formatting based on column type
          switch (col.format) {
            case 'date':
              value = value ? formatKoreanDate(value) : '';
              break;
            case 'currency':
              value = typeof value === 'number' ? formatKoreanCurrency(value) : value;
              break;
            case 'number':
              value = typeof value === 'number' ? value.toLocaleString('ko-KR') : value;
              break;
            default:
              break;
          }

          processedRow[col.header] = value;
        });
        return processedRow;
      });
    }

    const ws = XLSX.utils.json_to_sheet(processedData);

    // Set column widths
    if (columns.length > 0) {
      const colWidths = columns.map(col => ({
        wch: col.width || 15
      }));
      ws['!cols'] = colWidths;
    }

    XLSX.utils.book_append_sheet(wb, ws, sheetName);
  });

  const timestamp = new Date().toISOString().slice(0, 19).replace(/[:-]/g, '').replace('T', '_');
  const filename = `ì¢…í•©_ë°ì´í„°_${timestamp}.xlsx`;

  XLSX.writeFile(wb, filename);
};

