import { NextRequest, NextResponse } from 'next/server';
import { getSupabaseClient } from '@/lib/db-unified';
import {
  PurchaseTransactionUpdateSchema
} from '@/lib/validation';
import {
  createValidatedRoute,
  createSuccessResponse,
  createErrorResponse
} from '@/lib/validationMiddleware';

/**
 * GET /api/purchase-transactions/[id]
 * Retrieve a single purchase transaction by ID
 */
export async function GET(
  request: NextRequest,
  context: { params: Promise<{ id: string }> }
): Promise<NextResponse> {
  try {
    const { id } = await context.params;
    const transactionId = parseInt(id, 10);

    if (isNaN(transactionId)) {
      return createErrorResponse('ìœ íš¨í•˜ì§€ ì•Šì€ ê±°ë˜ IDì…ë‹ˆë‹¤.', 400);
    }

    const supabase = getSupabaseClient();

    // Fetch transaction with joined supplier and item details
    const { data, error } = await supabase
      .from('purchase_transactions')
      .select(`
        *,
        supplier:companies!supplier_id(
          company_id,
          company_name,
          company_code,
          business_number,
          representative,
          phone,
          email
        ),
        item:items!item_id(
          item_id,
          item_code,
          item_name,
          spec,
          unit,
          current_stock,
          safety_stock
        )
      `)
      .eq('transaction_id', transactionId)
      .eq('is_active', true)
      .single();

    if (error) {
      console.error('[purchase-transactions] Query error:', error);
      return createErrorResponse('ë§¤ì… ê±°ë˜ ì •ë³´ë¥¼ ì¡°íšŒí•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.', 500, error.message);
    }

    if (!data) {
      return createErrorResponse('ë§¤ì… ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
    }

    return createSuccessResponse(data);

  } catch (error) {
    console.error('[purchase-transactions] GET error:', error);
    return createErrorResponse('ë§¤ì… ê±°ë˜ ì¡°íšŒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.', 500);
  }
}

/**
 * PUT /api/purchase-transactions/[id]
 * Update a purchase transaction
 */
export async function PUT(
  request: NextRequest,
  context: { params: Promise<{ id: string }> }
): Promise<NextResponse> {
  try {
    const { id } = await context.params;
    const transactionId = parseInt(id, 10);

    if (isNaN(transactionId)) {
      return createErrorResponse('ìœ íš¨í•˜ì§€ ì•Šì€ ê±°ë˜ IDì…ë‹ˆë‹¤.', 400);
    }

    // Use request.text() + JSON.parse() for proper Korean character handling
    const text = await request.text();
    const data = JSON.parse(text);

    const supabase = getSupabaseClient();

    // Validate data against schema
    const validation = PurchaseTransactionUpdateSchema.safeParse({
      ...data,
      transaction_id: transactionId
    });

    if (!validation.success) {
      return createErrorResponse(
        'ì…ë ¥ê°’ì„ í™•ì¸í•´ì£¼ì„¸ìš”.',
        400,
        validation.error.issues.map(err => ({
          field: err.path.join('.'),
          message: err.message
        }))
      );
    }

    const validData = validation.data;

    // Check if transaction exists
    const { data: existingTransaction, error: checkError } = await supabase
      .from('purchase_transactions')
      .select('transaction_id, supplier_id, item_id')
      .eq('transaction_id', transactionId)
      .eq('is_active', true)
      .single();

    if (checkError || !existingTransaction) {
      return createErrorResponse('ìˆ˜ì •í•  ë§¤ì… ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
    }

    // Prepare update data
    const updateData: any = {
      updated_at: new Date().toISOString()
    };

    // If supplier_id changed, update cached supplier_name
    if (validData.supplier_id && validData.supplier_id !== existingTransaction.supplier_id) {
      const { data: supplier, error: supplierError } = await supabase
        .from('companies')
        .select('company_name')
        .eq('company_id', validData.supplier_id)
        .single();

      if (supplierError || !supplier) {
        return createErrorResponse('ê³µê¸‰ì‚¬ ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
      }

      updateData.supplier_id = validData.supplier_id;
      updateData.supplier_name = supplier.company_name;
    }

    // If item_id changed, update cached item details
    if (validData.item_id && validData.item_id !== existingTransaction.item_id) {
      const { data: item, error: itemError } = await supabase
        .from('items')
        .select('item_name, spec')
        .eq('item_id', validData.item_id)
        .single();

      if (itemError || !item) {
        return createErrorResponse('í’ˆëª© ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
      }

      updateData.item_id = validData.item_id;
      updateData.item_name = item.item_name;
      updateData.spec = item.spec;
    }

    // Add other updatable fields
    const updatableFields = [
      'transaction_date',
      'vehicle_model',
      'material_type',
      'quantity',
      'unit',
      'unit_price',
      'supply_amount',
      'tax_amount',
      'total_amount',
      'receiving_date',
      'warehouse_location',
      'tax_invoice_id',
      'tax_invoice_received',
      'payment_status',
      'paid_amount',
      'payment_due_date',
      'notes'
    ];

    for (const field of updatableFields) {
      if (validData[field as keyof typeof validData] !== undefined) {
        updateData[field] = validData[field as keyof typeof validData];
      }
    }

    // Update transaction
    const { data: updatedTransaction, error: updateError } = await supabase
      .from('purchase_transactions')
      .update(updateData)
      .eq('transaction_id', transactionId)
      .select()
      .single();

    if (updateError || !updatedTransaction) {
      console.error('[purchase-transactions] Update error:', updateError);
      return createErrorResponse('ë§¤ì… ê±°ë˜ ì •ë³´ë¥¼ ìˆ˜ì •í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.', 500, updateError?.message);
    }

    return createSuccessResponse(
      updatedTransaction,
      'ë§¤ì… ê±°ë˜ ì •ë³´ê°€ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤.'
    );

  } catch (error) {
    console.error('[purchase-transactions] PUT error:', error);
    return createErrorResponse('ë§¤ì… ê±°ë˜ ìˆ˜ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.', 500);
  }
}

/**
 * DELETE /api/purchase-transactions/[id]
 * Soft delete a purchase transaction
 */
export async function DELETE(
  request: NextRequest,
  context: { params: Promise<{ id: string }> }
): Promise<NextResponse> {
  try {
    const { id } = await context.params;
    const transactionId = parseInt(id, 10);

    if (isNaN(transactionId)) {
      return createErrorResponse('ìœ íš¨í•˜ì§€ ì•Šì€ ê±°ë˜ IDì…ë‹ˆë‹¤.', 400);
    }

    const supabase = getSupabaseClient();

    // Soft delete: set is_active to false
    const { data, error } = await supabase
      .from('purchase_transactions')
      .update({
        is_active: false,
        updated_at: new Date().toISOString()
      })
      .eq('transaction_id', transactionId)
      .select('transaction_id')
      .single();

    if (error) {
      console.error('[purchase-transactions] Delete error:', error);
      return createErrorResponse('ë§¤ì… ê±°ë˜ë¥¼ ë¹„í™œì„±í™”í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.', 500, error.message);
    }

    if (!data) {
      return createErrorResponse('ì‚­ì œí•  ë§¤ì… ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.', 404);
    }

    return createSuccessResponse(
      { transaction_id: transactionId },
      'ë§¤ì… ê±°ë˜ê°€ ì‚­ì œë˜ì—ˆìŠµë‹ˆë‹¤.'
    );

  } catch (error) {
    console.error('[purchase-transactions] DELETE error:', error);
    return createErrorResponse('ë§¤ì… ê±°ë˜ ì‚­ì œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.', 500);
  }
}

