import { NextRequest, NextResponse } from 'next/server';
import * as XLSX from 'xlsx';
import { createClient } from '@supabase/supabase-js';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const search = searchParams.get('search');
    const payment_method = searchParams.get('payment_method');
    const start_date = searchParams.get('start_date');
    const end_date = searchParams.get('end_date');

    // Initialize Supabase client
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Build query with joins
    let query = supabase
      .from('payments')
      .select(`
        payment_id,
        payment_no,
        payment_date,
        paid_amount,
        payment_method,
        bank_name,
        account_number,
        check_number,
        card_number,
        notes,
        is_active,
        created_at,
        updated_at,
        purchase_transaction:purchase_transactions!purchase_transaction_id(
          transaction_no,
          transaction_date,
          total_amount,
          payment_status,
          paid_amount
        ),
        supplier:companies!supplier_id(
          company_name,
          company_code,
          business_number
        )
      `)
      .eq('is_active', true);

    // Apply filters
    if (start_date) {
      query = query.gte('payment_date', start_date);
    }

    if (end_date) {
      query = query.lte('payment_date', end_date);
    }

    if (payment_method) {
      query = query.eq('payment_method', payment_method);
    }

    if (search) {
      query = query.or(`payment_no.ilike.%${search}%`);
    }

    // Apply ordering
    query = query.order('payment_date', { ascending: false });

    const { data: payments, error } = await query;

    if (error) {
      throw new Error(`Database query failed: ${error.message}`);
    }

    // Transform data with Korean headers
    const formattedPayments = payments?.map((payment: any) => ({
      "ì§€ê¸‰ID": payment.payment_id,
      "ì§€ê¸‰ë²ˆí˜¸": payment.payment_no,
      "ì§€ê¸‰ì¼ì": payment.payment_date,
      "ë§¤ì…ë²ˆí˜¸": payment.purchase_transaction?.transaction_no || '',
      "ë§¤ì…ì¼ì": payment.purchase_transaction?.transaction_date || '',
      "ê³µê¸‰ì‚¬ëª…": payment.supplier?.company_name || '',
      "ì‚¬ì—…ìë²ˆí˜¸": payment.supplier?.business_number || '',
      "ì§€ê¸‰ê¸ˆì•¡": payment.paid_amount,
      "ê²°ì œë°©ë²•": payment.payment_method === 'CASH' ? 'í˜„ê¸ˆ' :
                   payment.payment_method === 'TRANSFER' ? 'ê³„ì¢Œì´ì²´' :
                   payment.payment_method === 'CHECK' ? 'ìˆ˜í‘œ' :
                   payment.payment_method === 'CARD' ? 'ì¹´ë“œ' : payment.payment_method,
      "ì€í–‰ëª…": payment.bank_name || '',
      "ê³„ì¢Œë²ˆí˜¸": payment.account_number || '',
      "ìˆ˜í‘œë²ˆí˜¸": payment.check_number || '',
      "ì¹´ë“œë²ˆí˜¸": payment.card_number || '',
      "ë¹„ê³ ": payment.notes || '',
      "ë§¤ì…ê¸ˆì•¡": payment.purchase_transaction?.total_amount || 0,
      "ëˆ„ì ì§€ê¸‰ì•¡": payment.purchase_transaction?.paid_amount || 0,
      "ì§€ê¸‰ìƒíƒœ": payment.purchase_transaction?.payment_status === 'PENDING' ? 'ë¯¸ì§€ê¸‰' :
                 payment.purchase_transaction?.payment_status === 'PARTIAL' ? 'ë¶€ë¶„ì§€ê¸‰' :
                 payment.purchase_transaction?.payment_status === 'COMPLETED' ? 'ì™„ë£Œ' : '',
      "ë“±ë¡ì¼ì‹œ": new Date(payment.created_at).toLocaleString('ko-KR'),
      "ìˆ˜ì •ì¼ì‹œ": new Date(payment.updated_at).toLocaleString('ko-KR')
    })) || [];

    // Create workbook and worksheet
    const workbook = XLSX.utils.book_new();
