import { NextResponse } from 'next/server';

/**
 * UTF-8 ì¸ì½”ë”©ì„ ëª…ì‹œì ìœ¼ë¡œ ì„¤ì •í•œ JSON ì‘ë‹µ ìƒì„±
 */
export function createUTF8Response(data: any, status: number = 200): NextResponse {
  const response = NextResponse.json(data, { status });

  // UTF-8 ì¸ì½”ë”© ëª…ì‹œì  ì„¤ì •
  response.headers.set('Content-Type', 'application/json; charset=utf-8');
  response.headers.set('Content-Encoding', 'utf-8');

  return response;
}

/**
 * ì„±ê³µ ì‘ë‹µ ìƒì„±
 */
export function createSuccessResponse(data: any, message?: string): NextResponse {
  return createUTF8Response({
    success: true,
    data,
    message: message || 'ì„±ê³µì ìœ¼ë¡œ ì²˜ë¦¬ë˜ì—ˆìŠµë‹ˆë‹¤.'
  });
}

/**
 * ì—ëŸ¬ ì‘ë‹µ ìƒì„±
 */
export function createErrorResponse(error: string, status: number = 500): NextResponse {
  return createUTF8Response({
    success: false,
    error,
    timestamp: new Date().toISOString()
  }, status);
}

/**
 * í˜ì´ì§€ë„¤ì´ì…˜ ì‘ë‹µ ìƒì„±
 */
export function createPaginatedResponse(
  data: any[],
  total: number,
  page: number,
  limit: number
): NextResponse {
  const totalPages = Math.ceil(total / limit);

  return createUTF8Response({
    success: true,
    data,
    pagination: {
      page,
      limit,
      total,
      totalPages,
      hasNext: page < totalPages,
      hasPrev: page > 1
    }
  });
}

/**
 * íŒŒì¼ ë‹¤ìš´ë¡œë“œ ì‘ë‹µ ìƒì„±
 */
export function createFileResponse(
  buffer: Buffer,
  filename: string,
  contentType: string = 'application/octet-stream'
): NextResponse {
  const response = new NextResponse(buffer);

  // UTF-8 ì¸ì½”ë”© ëª…ì‹œì  ì„¤ì •
  response.headers.set('Content-Type', `${contentType}; charset=utf-8`);
  response.headers.set('Content-Disposition', `attachment; filename="${encodeURIComponent(filename)}"`);
  response.headers.set('Content-Encoding', 'utf-8');

  return response;
}

/**
 * Excel íŒŒì¼ ë‹¤ìš´ë¡œë“œ ì‘ë‹µ ìƒì„±
 */
export function createExcelResponse(buffer: Buffer, filename: string): NextResponse {
  return createFileResponse(
    buffer,
    filename,
    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
  );
}

