    });

    return NextResponse.json({
      error: 'ë©”íŠ¸ë¦­ìŠ¤ ë¦¬ì…‹ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤',
      details: errorMessage,
      timestamp: new Date().toISOString(),
      responseTime
    }, {
      status: 500,
