// ERP ì‹œìŠ¤í…œ ì¿¼ë¦¬ ìµœì í™” ìœ í‹¸ë¦¬í‹°
// í˜ì´ì§• ìµœì í™”, ë°°ì¹˜ ì²˜ë¦¬, ìºì‹± ì§€ì›

import { query } from './db-unified';

// =============================================================================
// 1. ì¿¼ë¦¬ ìµœì í™” ìœ í‹¸ë¦¬í‹°
// =============================================================================

export interface QueryOptimizationOptions {
  useCache?: boolean;
  cacheKey?: string;
  cacheTTL?: number; // seconds
  enableExplain?: boolean;
  timeout?: number; // milliseconds
  readPreference?: 'primary' | 'secondary';
}

export interface PaginationConfig {
  page: number;
  limit: number;
  orderBy?: string;
  direction?: 'ASC' | 'DESC';
  offset?: number;
}

export interface QueryResult<T> {
  data: T[];
  total: number;
  page: number;
  limit: number;
  totalPages: number;
  hasNextPage: boolean;
  hasPrevPage: boolean;
  executionTime: number;
  fromCache: boolean;
}

export interface ExplainResult {
  id: number;
  select_type: string;
  table: string;
  partitions?: string;
  type: string;
  possible_keys?: string;
  key?: string;
  key_len?: string;
  ref?: string;
  rows: number;
  filtered: number;
  Extra?: string;
}

// ë©”ëª¨ë¦¬ ìºì‹œ (í”„ë¡œë•ì…˜ì—ì„œëŠ” Redis ì‚¬ìš© ê¶Œì¥)
class QueryCache {
  private cache = new Map<string, { data: any; expiry: number }>();
  private maxSize = 1000;

  set(key: string, data: any, ttl: number = 300): void {
    // LRU ë°©ì‹ìœ¼ë¡œ ìºì‹œ í¬ê¸° ê´€ë¦¬
    if (this.cache.size >= this.maxSize) {
      const firstKey = this.cache.keys().next().value as string | undefined;
      if (firstKey) {
        this.cache.delete(firstKey);
      }
    }

    const expiry = Date.now() + (ttl * 1000);
    this.cache.set(key, { data, expiry });
  }

  get(key: string): any | null {
    const item = this.cache.get(key);
    if (!item) return null;

    if (Date.now() > item.expiry) {
      this.cache.delete(key);
      return null;
    }

    return item.data;
  }

  clear(): void {
    this.cache.clear();
  }

  size(): number {
    return this.cache.size;
  }

  // ë§Œë£Œëœ ìºì‹œ ì •ë¦¬
  cleanup(): void {
    const now = Date.now();
    for (const [key, item] of this.cache.entries()) {
      if (now > item.expiry) {
        this.cache.delete(key);
      }
    }
  }
}

const queryCache = new QueryCache();

// ìºì‹œ ì •ë¦¬ ì‘ì—… (5ë¶„ë§ˆë‹¤)
setInterval(() => {
  queryCache.cleanup();
}, 5 * 60 * 1000);

// =============================================================================
// 2. ìµœì í™”ëœ í˜ì´ì§• ì¿¼ë¦¬
// =============================================================================

export class OptimizedPagination {
  /**
   * ì»¤ì„œ ê¸°ë°˜ í˜ì´ì§• (ëŒ€ìš©ëŸ‰ ë°ì´í„°ì— ì í•©)
   */
  static async cursorPaginate<T>(
    baseQuery: string,
    countQuery: string,
    params: unknown[],
    config: {
      cursorColumn: string;
      cursorValue?: any;
      limit: number;
      direction?: 'next' | 'prev';
    },
    options: QueryOptimizationOptions = {}
  ): Promise<QueryResult<T>> {
    const startTime = Date.now();
    const cacheKey = options.cacheKey ||
      `cursor_${Buffer.from(baseQuery + JSON.stringify(params) + JSON.stringify(config)).toString('base64')}`;

    // ìºì‹œ í™•ì¸
    if (options.useCache) {
      const cached = queryCache.get(cacheKey);
      if (cached) {
        return {
          ...cached,
          executionTime: Date.now() - startTime,
          fromCache: true
        };
      }
    }

    const { cursorColumn, cursorValue, limit, direction = 'next' } = config;
    let modifiedQuery = baseQuery;
    const modifiedParams = [...params];

    // ì»¤ì„œ ì¡°ê±´ ì¶”ê°€
    if (cursorValue !== undefined) {
      const operator = direction === 'next' ? '>' : '<';
      const orderDirection = direction === 'next' ? 'ASC' : 'DESC';

      modifiedQuery += ` AND ${cursorColumn} ${operator} ?`;
      modifiedParams.push(cursorValue);

      modifiedQuery += ` ORDER BY ${cursorColumn} ${orderDirection}`;
    } else {
      modifiedQuery += ` ORDER BY ${cursorColumn} ASC`;
    }

    modifiedQuery += ` LIMIT ${limit + 1}`; // +1ë¡œ ë‹¤ìŒ í˜ì´ì§€ ì¡´ì¬ ì—¬ë¶€ í™•ì¸

    const [data, totalResult] = await Promise.all([
      query<T>(modifiedQuery, modifiedParams),
      query<{ total: number }>(countQuery, params)
    ]);

    const total = totalResult[0]?.total || 0;
    const hasMore = data.length > limit;
    const actualData = hasMore ? data.slice(0, limit) : data;

    const result: QueryResult<T> = {
      data: actualData,
      total,
      page: 0, // ì»¤ì„œ ê¸°ë°˜ì—ì„œëŠ” page ê°œë…ì´ ë‹¤ë¦„
      limit,
      totalPages: Math.ceil(total / limit),
      hasNextPage: hasMore && direction === 'next',
      hasPrevPage: cursorValue !== undefined && direction === 'prev',
      executionTime: Date.now() - startTime,
      fromCache: false
    };

    // ìºì‹œ ì €ì¥
    if (options.useCache) {
      queryCache.set(cacheKey, result, options.cacheTTL || 300);
    }

    return result;
  }

  /**
   * ì˜¤í”„ì…‹ ê¸°ë°˜ í˜ì´ì§• ìµœì í™” (ì‘ì€ ì˜¤í”„ì…‹ì— ì í•©)
   */
  static async offsetPaginate<T>(
    baseQuery: string,
    countQuery: string,
    params: unknown[],
    config: PaginationConfig,
    options: QueryOptimizationOptions = {}
  ): Promise<QueryResult<T>> {
    const startTime = Date.now();
    const { page, limit, orderBy, direction = 'ASC' } = config;
    const offset = (page - 1) * limit;

    const cacheKey = options.cacheKey ||
      `offset_${Buffer.from(baseQuery + JSON.stringify(params) + JSON.stringify(config)).toString('base64')}`;

    // ìºì‹œ í™•ì¸
    if (options.useCache) {
      const cached = queryCache.get(cacheKey);
      if (cached) {
        return {
          ...cached,
          executionTime: Date.now() - startTime,
          fromCache: true
        };
      }
    }

    // í° ì˜¤í”„ì…‹ ìµœì í™”
    let optimizedQuery = baseQuery;
    const optimizedParams = [...params];

    if (offset > 10000 && orderBy) {
      // ì„œë¸Œì¿¼ë¦¬ë¥¼ ì‚¬ìš©í•œ ì˜¤í”„ì…‹ ìµœì í™”
      optimizedQuery = `
        SELECT * FROM (${baseQuery}) AS t1
        WHERE t1.${orderBy} >= (
          SELECT ${orderBy} FROM (${baseQuery}) AS t2
          ORDER BY ${orderBy} ${direction}
          LIMIT 1 OFFSET ${offset}
        )
        ORDER BY t1.${orderBy} ${direction}
        LIMIT ${limit}
      `;
    } else {
      if (orderBy) {
        optimizedQuery += ` ORDER BY ${orderBy} ${direction}`;
      }
      optimizedQuery += ` LIMIT ${limit} OFFSET ${offset}`;
    }

    const [data, totalResult] = await Promise.all([
      query<T>(optimizedQuery, optimizedParams),
      query<{ total: number }>(countQuery, params)
    ]);

    const total = totalResult[0]?.total || 0;
    const totalPages = Math.ceil(total / limit);

    const result: QueryResult<T> = {
      data,
      total,
      page,
      limit,
      totalPages,
      hasNextPage: page < totalPages,
      hasPrevPage: page > 1,
      executionTime: Date.now() - startTime,
      fromCache: false
    };

    // ìºì‹œ ì €ì¥
    if (options.useCache) {
      queryCache.set(cacheKey, result, options.cacheTTL || 300);
    }

    return result;
  }
}

