export class QueryAnalyzer {
  /**
   * ì¿¼ë¦¬ ì‹¤í–‰ ê³„íš ë¶„ì„
   */
  static async explainQuery(sql: string, params: unknown[] = []): Promise<ExplainResult[]> {
    const explainSql = `EXPLAIN ${sql}`;
    return await query<ExplainResult>(explainSql, params);
  }

  /**
   * ì¿¼ë¦¬ ì„±ëŠ¥ ë¶„ì„
   */
  static async analyzeQueryPerformance(
    sql: string,
    params: unknown[] = [],
    iterations: number = 5
  ): Promise<{
    avgExecutionTime: number;
    minExecutionTime: number;
    maxExecutionTime: number;
    explainPlan: ExplainResult[];
    recommendations: string[];
  }> {
    const executionTimes: number[] = [];

    // ì—¬ëŸ¬ ë²ˆ ì‹¤í–‰í•˜ì—¬ í‰ê·  ì„±ëŠ¥ ì¸¡ì •
    for (let i = 0; i < iterations; i++) {
      const startTime = Date.now();
      await query(sql, params);
      executionTimes.push(Date.now() - startTime);
    }

    const explainPlan = await this.explainQuery(sql, params);
    const recommendations = this.generateRecommendations(explainPlan);

    return {
      avgExecutionTime: executionTimes.reduce((a, b) => a + b, 0) / iterations,
      minExecutionTime: Math.min(...executionTimes),
      maxExecutionTime: Math.max(...executionTimes),
      explainPlan,
      recommendations
    };
  }

  /**
   * ì¿¼ë¦¬ ìµœì í™” ì œì•ˆ ìƒì„±
   */
  private static generateRecommendations(explainPlan: ExplainResult[]): string[] {
    const recommendations: string[] = [];

    for (const step of explainPlan) {
      // í’€ í…Œì´ë¸” ìŠ¤ìº” ê°ì§€
      if (step.type === 'ALL') {
        recommendations.push(`í…Œì´ë¸” '${step.table}'ì—ì„œ í’€ ìŠ¤ìº”ì´ ë°œìƒí•©ë‹ˆë‹¤. ì¸ë±ìŠ¤ ì¶”ê°€ë¥¼ ê³ ë ¤í•˜ì„¸ìš”.`);
      }

      // ë†’ì€ í–‰ ìˆ˜ ê°ì§€
      if (step.rows > 10000) {
        recommendations.push(`í…Œì´ë¸” '${step.table}'ì—ì„œ ${step.rows}í–‰ì„ ê²€ì‚¬í•©ë‹ˆë‹¤. WHERE ì¡°ê±´ì„ ì¶”ê°€í•˜ê±°ë‚˜ ì¸ë±ìŠ¤ë¥¼ ìµœì í™”í•˜ì„¸ìš”.`);
      }

      // ì„ì‹œ í…Œì´ë¸” ì‚¬ìš© ê°ì§€
      if (step.Extra?.includes('Using temporary')) {
        recommendations.push(`ì„ì‹œ í…Œì´ë¸”ì´ ì‚¬ìš©ë©ë‹ˆë‹¤. GROUP BYë‚˜ ORDER BY ì ˆì„ ìµœì í™”í•˜ì„¸ìš”.`);
      }

      // íŒŒì¼ ì •ë ¬ ê°ì§€
      if (step.Extra?.includes('Using filesort')) {
        recommendations.push(`íŒŒì¼ ì •ë ¬ì´ ë°œìƒí•©ë‹ˆë‹¤. ORDER BYì— ì ì ˆí•œ ì¸ë±ìŠ¤ë¥¼ ì¶”ê°€í•˜ì„¸ìš”.`);
      }

      // ì¸ë±ìŠ¤ ì¡°ê±´ ë¯¸ì‚¬ìš© ê°ì§€
      if (step.key === null && step.possible_keys) {
        recommendations.push(`ê°€ëŠ¥í•œ ì¸ë±ìŠ¤ê°€ ìˆì§€ë§Œ ì‚¬ìš©ë˜ì§€ ì•ŠìŠµë‹ˆë‹¤. ì¿¼ë¦¬ ì¡°ê±´ì„ ê²€í† í•˜ì„¸ìš”.`);
      }
    }

    return recommendations;
  }

  /**
   * ìŠ¬ë¡œìš° ì¿¼ë¦¬ íƒì§€
   */
  static async detectSlowQueries(thresholdMs: number = 1000): Promise<any[]> {
    const sql = `
      SELECT
        sql_text,
        exec_count,
        avg_timer_wait / 1000000000 as avg_time_ms,
        max_timer_wait / 1000000000 as max_time_ms,
        sum_timer_wait / 1000000000 as total_time_ms,
        sum_rows_examined,
        sum_rows_sent,
        digest
      FROM performance_schema.events_statements_summary_by_digest
      WHERE avg_timer_wait / 1000000000 > ?
      ORDER BY avg_timer_wait DESC
      LIMIT 20
    `;

    try {
      return await query(sql, [thresholdMs]);
    } catch (error) {
      console.warn('Performance schemaë¥¼ ì‚¬ìš©í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤:', error);
      return [];
    }
  }
}

