export class QueryMonitor {
  private static queryStats = new Map<string, {
    count: number;
    totalTime: number;
    avgTime: number;
    lastExecuted: Date;
  }>();

  /**
   * ì¿¼ë¦¬ ì‹¤í–‰ í†µê³„ ìˆ˜ì§‘
   */
  static recordQuery(queryHash: string, executionTime: number): void {
    const existing = this.queryStats.get(queryHash) || {
      count: 0,
      totalTime: 0,
      avgTime: 0,
      lastExecuted: new Date()
    };

    existing.count += 1;
    existing.totalTime += executionTime;
    existing.avgTime = existing.totalTime / existing.count;
    existing.lastExecuted = new Date();

    this.queryStats.set(queryHash, existing);
  }

  /**
   * ì¿¼ë¦¬ í†µê³„ ì¡°íšŒ
   */
  static getQueryStats(): Array<{
    queryHash: string;
    count: number;
    totalTime: number;
    avgTime: number;
    lastExecuted: Date;
  }> {
    return Array.from(this.queryStats.entries()).map(([queryHash, stats]) => ({
      queryHash,
      ...stats
    }));
  }

  /**
   * ìºì‹œ í†µê³„ ì¡°íšŒ
   */
  static getCacheStats(): {
    size: number;
    hitRate?: number;
  } {
    return {
      size: queryCache.size()
    };
  }

  /**
   * ì„±ëŠ¥ ë¦¬í¬íŠ¸ ìƒì„±
   */
  static generatePerformanceReport(): {
    slowQueries: any[];
    cacheStats: any;
    queryStats: any[];
    recommendations: string[];
  } {
    const queryStats = this.getQueryStats();
    const slowQueries = queryStats
      .filter(q => q.avgTime > 1000)
      .sort((a, b) => b.avgTime - a.avgTime);

    const recommendations: string[] = [];

    if (slowQueries.length > 0) {
      recommendations.push(`${slowQueries.length}ê°œì˜ ëŠë¦° ì¿¼ë¦¬ê°€ ê°ì§€ë˜ì—ˆìŠµë‹ˆë‹¤.`);
    }

    const highFrequencyQueries = queryStats
      .filter(q => q.count > 100)
      .sort((a, b) => b.count - a.count);

    if (highFrequencyQueries.length > 0) {
      recommendations.push('ìì£¼ ì‹¤í–‰ë˜ëŠ” ì¿¼ë¦¬ì— ëŒ€í•´ ìºì‹±ì„ ê³ ë ¤í•˜ì„¸ìš”.');
    }

    return {
      slowQueries,
      cacheStats: this.getCacheStats(),
      queryStats: queryStats.slice(0, 20), // ìƒìœ„ 20ê°œë§Œ
      recommendations
    };
  }
}

