const formatCurrency = (amount: number): string => {
  if (amount >= 1000000000) {
    return `â‚©${(amount / 1000000000).toFixed(1)}ì‹­ì–µ`;
  } else if (amount >= 100000000) {
    return `â‚©${(amount / 100000000).toFixed(1)}ì–µ`;
  } else if (amount >= 10000000) {
