      return NextResponse.json({
        success: false,
        error: 'ìˆ˜ëŸ‰ì€ 0ë³´ë‹¤ ì»¤ì•¼ í•©ë‹ˆë‹¤.'
      }, { status: 400 });
    }

    if (unit_price < 0) {
      return NextResponse.json({
        success: false,
        error: 'ë‹¨ê°€ëŠ” 0 ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤.'
