import { NextRequest, NextResponse } from 'next/server';
import * as XLSX from 'xlsx';
import { createClient } from '@supabase/supabase-js';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const type = searchParams.get('type');
    const startDate = searchParams.get('start_date');
    const endDate = searchParams.get('end_date');
    const itemId = searchParams.get('item_id');

    // Initialize Supabase client for safe queries
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
    const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Build safe query using Supabase client
    let query = supabase
      .from('inventory_transactions')
      .select(`
        transaction_id,
        transaction_date,
        transaction_type,
        quantity,
        unit_price,
        total_amount,
        from_location,
        to_location,
        warehouse_from,
        warehouse_to,
        lot_no,
        expiry_date,
        reference_no,
        reference_id,
        notes,
        created_at,
        items!left(item_code, item_name, spec, unit),
        companies!left(company_name),
        users!left(full_name)
      `);

    // Apply filters safely
    if (type) {
      query = query.eq('transaction_type', type);
    }

    if (startDate) {
      query = query.gte('transaction_date', startDate);
    }

    if (endDate) {
      query = query.lte('transaction_date', endDate);
    }

    if (itemId) {
      query = query.eq('item_id', parseInt(itemId));
