import { NextRequest, NextResponse } from 'next/server';
import * as XLSX from 'xlsx';
import { mcp__supabase__execute_sql } from '@/lib/supabase-mcp';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const month = searchParams.get('month') || new Date().toISOString().slice(0, 7);
    const category = searchParams.get('category');

    // Query v_monthly_accounting VIEW
    const companyQuery = `
      SELECT
        month,
        company_category,
        company_name,
        company_code,
        business_info,
        sales_amount,
        purchase_amount,
        net_amount
      FROM v_monthly_accounting
      WHERE month = '${month}'
      ${category ? `AND company_category = '${category}'` : ''}
      ORDER BY ABS(net_amount) DESC
    `;

    const companyData = await mcp__supabase__execute_sql({
      project_id: process.env.SUPABASE_PROJECT_ID!,
      query: companyQuery
    });

    // Query v_category_monthly_summary VIEW for stats
    const categoryQuery = `
      SELECT
        company_category,
        total_sales,
        total_purchases,
        total_companies
      FROM v_category_monthly_summary
      WHERE month = '${month}'
      ORDER BY company_category
    `;

    const categoryData = await mcp__supabase__execute_sql({
      project_id: process.env.SUPABASE_PROJECT_ID!,
      query: categoryQuery
    });

    // Calculate total statistics
    const totalSales = companyData.reduce((sum: number, row: any) => sum + (row.sales_amount || 0), 0);
    const totalPurchases = companyData.reduce((sum: number, row: any) => sum + (row.purchase_amount || 0), 0);
    const netAmount = totalSales - totalPurchases;
    const totalCompanies = new Set(companyData.map((row: any) => row.company_code)).size;

    // Create workbook
    const workbook = XLSX.utils.book_new();

    // Sheet 1: Metadata (ë‚´ë³´ë‚´ê¸° ì •ë³´)
    const metadataRows = [
      ['ë‚´ë³´ë‚´ê¸° ì •ë³´', ''],
      ['ë‚´ë³´ë‚¸ ë‚ ì§œ', new Date().toLocaleString('ko-KR')],
      ['ì¡°íšŒ ì›”', month],
      ['í•„í„° ë¶„ë¥˜', category || 'ì „ì²´'],
      ['ì´ ë ˆì½”ë“œ ìˆ˜', companyData.length],
      ['', ''],
      ['íƒœì°½ ERP ì‹œìŠ¤í…œ', 'ì›”ë³„ íšŒê³„ ì§‘ê³„ ë‚´ë³´ë‚´ê¸°']
    ];

    const metadataSheet = XLSX.utils.aoa_to_sheet(metadataRows);
    metadataSheet['!cols'] = [{ wch: 15 }, { wch: 30 }];

    // Sheet 2: Statistics (í†µê³„)
    const statsRows = [
      ['í†µê³„ í•­ëª©', 'ê°’'],
      ['ì´ ë§¤ì¶œ', `â‚©${totalSales.toLocaleString('ko-KR')}`],
      ['ì´ ë§¤ì…', `â‚©${totalPurchases.toLocaleString('ko-KR')}`],
      ['ìˆœì´ìµ', `â‚©${netAmount.toLocaleString('ko-KR')}`],
      ['ê±°ë˜ì²˜ ìˆ˜', `${totalCompanies}ê°œ`],
      ['', ''],
      ['ë¶„ë¥˜ë³„ ì§‘ê³„', ''],
    ];

    // Add category breakdown to stats
    categoryData.forEach((cat: any) => {
      const categoryNet = cat.total_sales - cat.total_purchases;
      statsRows.push([
        cat.company_category,
        `ë§¤ì¶œ: â‚©${cat.total_sales.toLocaleString('ko-KR')} | ë§¤ì…: â‚©${cat.total_purchases.toLocaleString('ko-KR')} | ìˆœ: â‚©${categoryNet.toLocaleString('ko-KR')}`
      ]);
    });

    statsRows.push(['', '']);
    statsRows.push(['í‰ê·  ê±°ë˜ì•¡', totalCompanies > 0 ? `â‚©${Math.round(netAmount / totalCompanies).toLocaleString('ko-KR')}` : 'â‚©0']);

    const statsSheet = XLSX.utils.aoa_to_sheet(statsRows);
    statsSheet['!cols'] = [{ wch: 20 }, { wch: 60 }];

    // Sheet 3: Data (ì›”ë³„ íšŒê³„ ë‚´ì—­) with Korean headers
    const koreanData = companyData.map((row: any) => {
      // Parse business_info JSONB field
      let businessType = '';
      let businessItem = '';

      if (row.business_info) {
        try {
          const info = typeof row.business_info === 'string'
            ? JSON.parse(row.business_info)
            : row.business_info;
          businessType = info.business_type || '';
          businessItem = info.business_item || '';
        } catch (e) {
          // If parsing fails, leave empty
          console.warn('Failed to parse business_info:', e);
        }
      }

      return {
        'ì›”': row.month,
        'ê±°ë˜ì²˜ë¶„ë¥˜': row.company_category || '',
        'ê±°ë˜ì²˜ëª…': row.company_name || '',
        'ê±°ë˜ì²˜ì½”ë“œ': row.company_code || '',
        'ì—…íƒœ': businessType,
        'ì¢…ëª©': businessItem,
        'ë§¤ì¶œì•¡': row.sales_amount || 0,
        'ë§¤ì…ì•¡': row.purchase_amount || 0,
        'ìˆœì´ìµ': row.net_amount || 0
      };
    });

    const worksheet = XLSX.utils.json_to_sheet(koreanData);

    // Set column widths
    worksheet['!cols'] = [
      { wch: 10 },  // ì›”
      { wch: 18 },  // ê±°ë˜ì²˜ë¶„ë¥˜
      { wch: 25 },  // ê±°ë˜ì²˜ëª…
      { wch: 15 },  // ê±°ë˜ì²˜ì½”ë“œ
      { wch: 15 },  // ì—…íƒœ
      { wch: 15 },  // ì¢…ëª©
      { wch: 18 },  // ë§¤ì¶œì•¡
      { wch: 18 },  // ë§¤ì…ì•¡
      { wch: 18 }   // ìˆœì´ìµ
    ];

    // Append sheets to workbook
    XLSX.utils.book_append_sheet(workbook, metadataSheet, 'ë‚´ë³´ë‚´ê¸° ì •ë³´');
    XLSX.utils.book_append_sheet(workbook, statsSheet, 'í†µê³„');
    XLSX.utils.book_append_sheet(workbook, worksheet, 'ì›”ë³„ íšŒê³„ ë‚´ì—­');

    // Generate Excel file
    const excelBuffer = XLSX.write(workbook, {
      type: 'buffer',
      bookType: 'xlsx'
    });

    // Create filename with current date
    const filename = `ì›”ë³„íšŒê³„ì§‘ê³„_${month}${category ? `_${category}` : ''}.xlsx`;

    // Return Excel file
    return new NextResponse(excelBuffer, {
      status: 200,
      headers: {
        'Content-Type': 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'Content-Disposition': `attachment; filename="${encodeURIComponent(filename)}"`,
        'Content-Length': excelBuffer.length.toString()
      }
    });

  } catch (error) {
    console.error('Error exporting accounting data:', error);
    return NextResponse.json(
      {
        success: false,
        error: 'Failed to export accounting data to Excel'
      },
      { status: 500 }
    );
  }
}

