      'ì°¨ì¢…': item.vehicle_model ?? '-',
      'ê·œê²©/ì†Œì¬': item.spec ?? item.material ?? '-',
      'ë‹¨ìœ„': item.unit,
      'ë‘ê»˜(mm)': item.thickness ?? '-',
      'í­(mm)': item.width ?? '-',
      'ë‹¨ìœ„ì¤‘ëŸ‰(kg)': item.mm_weight ?? '-',
      'í˜„ì¬ê³ ': item.current_stock ?? 0,
      'ì•ˆì „ì¬ê³ ': item.safety_stock ?? 0,
      'ê¸°ì¤€ë‹¨ê°€': item.price ?? 0,
      'ë¹„ê³ ': item.description ?? '-',
      'ìƒì„±ì¼ì‹œ': item.created_at ? new Date(item.created_at).toLocaleString('ko-KR') : '-',
      'ìˆ˜ì •ì¼ì‹œ': item.updated_at ? new Date(item.updated_at).toLocaleString('ko-KR') : '-'
    }));

    const workbook = XLSX.utils.book_new();
    const worksheet = XLSX.utils.json_to_sheet(rows);
    worksheet['!cols'] = [
      { wch: 10 },
      { wch: 15 },
      { wch: 25 },
      { wch: 10 },
      { wch: 12 },
      { wch: 12 },
      { wch: 15 },
      { wch: 20 },
      { wch: 8 },
      { wch: 10 },
      { wch: 10 },
      { wch: 12 },
      { wch: 12 },
      { wch: 12 },
      { wch: 25 },
      { wch: 18 },
