    ];

    const metadataRows = [
      ['ìƒì„± ì¼ì‹œ', new Date().toLocaleString('ko-KR')],
      ['ì´ í’ˆëª© ìˆ˜', rows.length],
      ['ë¶„ë¥˜', category || 'ì „ì²´'],
      ['íƒ€ì…', itemType || 'ì „ì²´'],
      ['ì†Œì¬í˜•íƒœ', materialType || 'ì „ì²´'],
      ['ê²€ìƒ‰ì–´', search || 'ì—†ìŒ']
    ];

    const metadataSheet = XLSX.utils.aoa_to_sheet(metadataRows);
    metadataSheet['!cols'] = [{ wch: 15 }, { wch: 30 }];

    XLSX.utils.book_append_sheet(workbook, metadataSheet, 'ìš”ì•½');
    XLSX.utils.book_append_sheet(workbook, worksheet, 'í’ˆëª© ëª©ë¡');

    const excelBuffer = XLSX.write(workbook, {
