          { success: false, error: 'íŒë§¤ ê±°ë˜ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤' },
          { status: 400 }
        );
      }

      // Calculate total collected (excluding current collection)
      const { data: otherCollections } = await supabaseAdmin
        .from('collection_transactions')
        .select('collected_amount')
