      }

      // Determine new payment status
      if (remaining === 0) {
        newPaymentStatus = 'COMPLETED';
      } else if (remaining < salesTx.total_amount) {
        newPaymentStatus = 'PARTIAL';
      } else {
        newPaymentStatus = 'PENDING';
