    if (duplicateError) {
      throw new APIError('ì°½ê³  ì¤‘ë³µ ì—¬ë¶€ë¥¼ í™•ì¸í•˜ì§€ ëª»í–ˆìŠµë‹ˆë‹¤.', 500, duplicateError.message);
    }

    if (duplicate) {
      throw new APIError('ì´ë¯¸ ì‚¬ìš© ì¤‘ì¸ ì°½ê³  ì½”ë“œì…ë‹ˆë‹¤.', 409);
    }

    const now = new Date().toISOString();
    const newWarehouse: WarehouseInsert = {
      warehouse_code: warehouseCode,
      warehouse_name: warehouseName,
      warehouse_type: warehouseType,
      address: payload.address ?? null,
      manager_name: payload.manager_name ?? null,
