      max_capacity: maxCapacity,
      current_usage: currentUsage ?? 0,
      is_active: true,
      created_at: now,
      updated_at: now,
      created_by: payload.created_by ?? DEFAULT_USER_ID,
    };

    const { data, error } = await supabase
      .from('warehouses')
      .insert(newWarehouse)
      .select(
        `
        warehouse_id,
        warehouse_code,
